import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_ENDPOINTS } from '../config/api';

function CdDashboard() {
  const navigate = useNavigate();
  const [selectedDateFrom, setSelectedDateFrom] = useState(new Date().toISOString().slice(0, 10));
  const [selectedDateTo, setSelectedDateTo] = useState(new Date().toISOString().slice(0, 10));
  const [cashDrops, setCashDrops] = useState([]);
  const [cashDrawers, setCashDrawers] = useState([]);
  const [activeDate, setActiveDate] = useState(null); 
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const denoms = ['hundreds', 'fifties', 'twenties', 'tens', 'fives', 'twos', 'ones', 'half_dollars', 'quarters', 'dimes', 'nickels', 'pennies'];

  useEffect(() => {
    const checkAuth = async () => {
      const token = sessionStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }
      setLoading(false);
    };
    checkAuth();
  }, [navigate]);

  const fetchData = async (from, to) => {
    setError('');
    setActiveDate(null);
    try {
      const [dropResponse, drawerResponse] = await Promise.all([
        fetch(`${API_ENDPOINTS.CASH_DROP}?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
        fetch(`${API_ENDPOINTS.CASH_DRAWER}?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
      ]);

      if (!dropResponse.ok || !drawerResponse.ok) {
        const errorText = await dropResponse.text();
        throw new Error(`Server error: ${dropResponse.status} - ${errorText}`);
      }

      const dropData = await dropResponse.json();
      const drawerData = await drawerResponse.json();

      setCashDrops(dropData);
      setCashDrawers(drawerData);

      const allDates = [...new Set([...dropData.map(d => d.date), ...drawerData.map(d => d.date)])].sort().reverse();
      if (allDates.length > 0) setActiveDate(allDates[0]);
    } catch (err) {
      if (err.message === 'Failed to fetch' || err.name === 'TypeError') {
        setError('Cannot connect to server. Please make sure the backend is running on http://localhost:8000');
      } else {
        setError(err.message);
      }
      console.error('Fetch error:', err);
    }
  };

  const handleWTD = () => {
    const now = new Date();
    const day = now.getDay();
    const diff = now.getDate() - day + (day === 0 ? -6 : 1); 
    const start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const handleMTD = () => {
    const start = new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const handleYTD = () => {
    const start = new Date(new Date().getFullYear(), 0, 1).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const uniqueDates = [...new Set([...cashDrops.map(d => d.date), ...cashDrawers.map(d => d.date)])].sort().reverse();

  // Helper to group records by index to ensure horizontal row alignment
  const maxRows = Math.max(
    cashDrops.filter(d => d.date === activeDate).length,
    cashDrawers.filter(d => d.date === activeDate).length
  );

  if (loading) return <div className="min-h-screen bg-gray-50 flex items-center justify-center">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-950 p-4 font-sans text-gray-900 dark:text-gray-100">
      <div className="max-w-[1400px] mx-auto bg-white dark:bg-gray-900 rounded-lg shadow-xl border border-gray-200 dark:border-gray-800 overflow-hidden">

        {/* Header Section */}
        <div className="p-6 border-b border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900">
          <div className="flex flex-col md:flex-row justify-between items-center gap-6">
            <h2 className="text-2xl font-bold tracking-tight uppercase">Cash <span className="text-blue-600">Dashboard</span></h2>

            <div className="flex flex-wrap items-end gap-3 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
              <div className="flex flex-col">
                <label className="text-[10px] font-bold text-gray-400 uppercase mb-1">From</label>
                <input type="date" value={selectedDateFrom} onChange={e => setSelectedDateFrom(e.target.value)} className="bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded p-1.5 text-xs focus:ring-1 focus:ring-blue-500" />
              </div>
              <div className="flex flex-col">
                <label className="text-[10px] font-bold text-gray-400 uppercase mb-1">To</label>
                <input type="date" value={selectedDateTo} onChange={e => setSelectedDateTo(e.target.value)} className="bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded p-1.5 text-xs focus:ring-1 focus:ring-blue-500" />
              </div>
              <button onClick={() => fetchData(selectedDateFrom, selectedDateTo)} className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded font-bold text-xs transition-all">Fetch Data</button>

              <div className="flex gap-1 border-l pl-3 border-gray-300 dark:border-gray-600">
                <button onClick={handleWTD} className="px-3 py-2 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded text-[10px] font-bold hover:bg-gray-50 transition-colors">WTD</button>
                <button onClick={handleMTD} className="px-3 py-2 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded text-[10px] font-bold hover:bg-gray-50 transition-colors">MTD</button>
                <button onClick={handleYTD} className="px-3 py-2 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded text-[10px] font-bold hover:bg-gray-50 transition-colors">YTD</button>
              </div>
            </div>
          </div>
        </div>

        <div className="flex flex-col md:flex-row min-h-[700px]">

          {/* Left Sidebar: Dates */}
          <div className="w-full md:w-56 bg-gray-50 dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 p-4">
            <h4 className="text-[10px] font-bold text-gray-500 uppercase tracking-widest mb-4">Date Log</h4>
            <div className="space-y-1.5 max-h-[600px] overflow-y-auto pr-1 custom-scrollbar">
              {uniqueDates.map(date => (
                <button
                  key={date}
                  onClick={() => setActiveDate(date)}
                  className={`w-full text-left px-4 py-3 rounded text-xs font-bold transition-all ${
                    activeDate === date 
                    ? 'bg-blue-600 text-white shadow-md' 
                    : 'bg-white dark:bg-gray-800 text-gray-600 dark:text-gray-400 hover:border-blue-500 border border-gray-200 dark:border-gray-700'
                  }`}
                >
                  {new Date(date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' })}
                </button>
              ))}
            </div>
          </div>

          {/* Main Content Area */}
          <div className="flex-1 p-6 bg-white dark:bg-gray-950">
            {!activeDate ? (
              <div className="h-full flex flex-col items-center justify-center text-gray-400 italic text-sm">
                Select a date from the log to view details
              </div>
            ) : (
              <div className="max-w-6xl mx-auto">
                {/* Column Headers */}
                <div className="grid grid-cols-2 gap-8 mb-6">
                  <div className="flex items-center gap-2 border-b-2 border-blue-600 pb-2">
                    <span className="text-sm font-black uppercase tracking-widest">Cash Drops</span>
                  </div>
                  <div className="flex items-center gap-2 border-b-2 border-emerald-600 pb-2">
                    <span className="text-sm font-black uppercase tracking-widest">Cash Drawers</span>
                  </div>
                </div>

                {/* Data Rows: This logic ensures horizontal alignment */}
                <div className="space-y-6">
                  {[...Array(maxRows)].map((_, index) => {
                    const drop = cashDrops.filter(d => d.date === activeDate)[index];
                    const drawer = cashDrawers.filter(d => d.date === activeDate)[index];

                    return (
                      <div key={index} className="grid grid-cols-2 gap-8 items-stretch">

                        {/* Drop Card */}
                        <div className="h-full">
                          {drop ? (
                            <div className="h-full flex flex-col p-5 bg-gray-50 dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-lg shadow-sm">
                              <div className="flex justify-between items-start mb-4">
                                <span className="text-[10px] font-bold bg-blue-100 text-blue-700 px-2 py-0.5 rounded uppercase">Shift {drop.shift_number}</span>
                                <span className="text-2xl font-bold text-blue-600 tracking-tighter">${drop.drop_amount}</span>
                              </div>
                              <div className="text-[10px] font-bold text-gray-500 uppercase mb-4">
                                Station: {drop.workstation} | {drop.user_name}
                              </div>
                              <div className="grid grid-cols-2 gap-2 mt-auto pt-4 border-t border-gray-200 dark:border-gray-800">
                                {denoms.map(key => drop[key] > 0 && (
                                  <div key={key} className="flex justify-between text-[10px] bg-white dark:bg-gray-800 p-1.5 px-2 rounded border border-gray-100 dark:border-gray-700">
                                    <span className="capitalize text-gray-400">{key.replace('_', ' ')}</span>
                                    <span className="font-bold">{drop[key]}</span>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ) : <div className="h-full bg-gray-50/30 rounded-lg border border-dashed border-gray-200"></div>}
                        </div>

                        {/* Drawer Card */}
                        <div className="h-full">
                          {drawer ? (
                            <div className="h-full flex flex-col p-5 bg-gray-50 dark:bg-gray-900 border border-gray-200 dark:border-gray-800 rounded-lg shadow-sm">
                              <div className="flex justify-between items-start mb-4">
                                <span className="text-[10px] font-bold bg-emerald-100 text-emerald-700 px-2 py-0.5 rounded uppercase">Station {drawer.workstation}</span>
                                <span className="text-2xl font-bold text-emerald-600 tracking-tighter">${drawer.total_cash}</span>
                              </div>
                              <div className="text-[10px] font-bold text-gray-500 uppercase mb-4">
                                Initial: ${drawer.starting_cash} | {drawer.user_name}
                              </div>
                              <div className="grid grid-cols-2 gap-2 mt-auto pt-4 border-t border-gray-200 dark:border-gray-800">
                                {denoms.map(key => drawer[key] > 0 && (
                                  <div key={key} className="flex justify-between text-[10px] bg-white dark:bg-gray-800 p-1.5 px-2 rounded border border-gray-100 dark:border-gray-700">
                                    <span className="capitalize text-gray-400">{key.replace('_', ' ')}</span>
                                    <span className="font-bold">{drawer[key]}</span>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ) : <div className="h-full bg-gray-50/30 rounded-lg border border-dashed border-gray-200"></div>}
                        </div>

                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <style>{`
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #cbd5e1; border-radius: 4px; }
        .dark .custom-scrollbar::-webkit-scrollbar-thumb { background: #334155; }
      `}</style>
    </div>
  );
}

export default CdDashboard;
