import React, { useState, useEffect } from "react";
import { API_ENDPOINTS } from "../config/api";

const DENOMINATION_CONFIG = [
  { name: 'Hundreds', value: 100, field: 'hundreds' },
  { name: 'Fifties', value: 50, field: 'fifties' },
  { name: 'Twenties', value: 20, field: 'twenties' },
  { name: 'Tens', value: 10, field: 'tens' },
  { name: 'Fives', value: 5, field: 'fives' },
  { name: 'Twos', value: 2, field: 'twos' },
  { name: 'Ones', value: 1, field: 'ones' },
  { name: 'Half Dollars', value: 0.50, field: 'half_dollars' },
  { name: 'Quarters', value: 0.25, field: 'quarters' },
  { name: 'Dimes', value: 0.10, field: 'dimes' },
  { name: 'Nickels', value: 0.05, field: 'nickels' },
  { name: 'Pennies', value: 0.01, field: 'pennies' },
];

const BankDrop = () => {
  const [data, setData] = useState([]);
  const [dateFrom, setDateFrom] = useState(new Date().toISOString().slice(0, 10));
  const [dateTo, setDateTo] = useState(new Date().toISOString().slice(0, 10));
  const [loading, setLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedCashDrop, setSelectedCashDrop] = useState(null);
  const [editingDenominations, setEditingDenominations] = useState({});
  const [selectedIds, setSelectedIds] = useState(new Set());
  const [showSummaryModal, setShowSummaryModal] = useState(false);
  const [summaryData, setSummaryData] = useState(null);
  const [loadingSummary, setLoadingSummary] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    const token = localStorage.getItem('access_token');
    try {
      const response = await fetch(
        `${API_ENDPOINTS.BANK_DROP}?datefrom=${dateFrom}&dateto=${dateTo}`,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }
      const result = await response.json();
      setData(Array.isArray(result) ? result : []);
    } catch (err) {
      console.error("Fetch error:", err);
      if (err.message === 'Failed to fetch' || err.name === 'TypeError') {
        alert('Cannot connect to server. Please make sure the backend is running on http://localhost:8000');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleQuickFilter = (type) => {
    const now = new Date();
    let start = new Date();
    if (type === 'WTD') {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff));
    } else if (type === 'MTD') {
      start = new Date(now.getFullYear(), now.getMonth(), 1);
    }
    setDateFrom(start.toISOString().slice(0, 10));
    setDateTo(new Date().toISOString().slice(0, 10));
  };

  const handleBankDroppedToggle = async (item) => {
    const newValue = !item.bank_dropped;

    // If toggling to true, open modal to edit denominations
    if (newValue) {
      try {
        const token = localStorage.getItem('access_token');
        const response = await fetch(
          API_ENDPOINTS.BANK_DROP_CASH_DROP(item.drop_entry_id),
          { headers: { 'Authorization': `Bearer ${token}` } }
        );
        if (response.ok) {
          const cashDrop = await response.json();
          setSelectedCashDrop(cashDrop);
          setEditingDenominations({
            hundreds: cashDrop.hundreds || 0,
            fifties: cashDrop.fifties || 0,
            twenties: cashDrop.twenties || 0,
            tens: cashDrop.tens || 0,
            fives: cashDrop.fives || 0,
            twos: cashDrop.twos || 0,
            ones: cashDrop.ones || 0,
            half_dollars: cashDrop.half_dollars || 0,
            quarters: cashDrop.quarters || 0,
            dimes: cashDrop.dimes || 0,
            nickels: cashDrop.nickels || 0,
            pennies: cashDrop.pennies || 0,
          });
        } else {
          alert('Failed to load cash drop details');
        }
      } catch (error) {
        console.error('Error loading cash drop:', error);
        alert('Error loading cash drop details');
      }
    } else {
      // If toggling to false, just update the bank_dropped status
      await updateBankDroppedStatus(item.drop_entry_id, false);
    }
  };

  const updateBankDroppedStatus = async (cashDropId, status) => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(
        API_ENDPOINTS.BANK_DROP_UPDATE_DENOMINATIONS(cashDropId),
        {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({ bank_dropped: status })
        }
      );

      if (response.ok) {
        fetchData(); // Refresh data
      } else {
        const err = await response.json();
        alert(err.error || 'Failed to update bank dropped status');
      }
    } catch (error) {
      console.error('Error updating bank dropped status:', error);
      alert('Error updating bank dropped status');
    }
  };

  const handleSaveDenominations = async () => {
    if (!selectedCashDrop) return;

    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(
        API_ENDPOINTS.BANK_DROP_UPDATE_DENOMINATIONS(selectedCashDrop.id),
        {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({
            ...editingDenominations,
            bank_dropped: true
          })
        }
      );

      if (response.ok) {
        setSelectedCashDrop(null);
        setEditingDenominations({});
        fetchData(); // Refresh data
        alert('Denominations updated and marked as bank dropped');
      } else {
        const err = await response.json();
        alert(err.error || 'Failed to update denominations');
      }
    } catch (error) {
      console.error('Error saving denominations:', error);
      alert('Error saving denominations');
    }
  };

  const handleSelectAll = (checked) => {
    if (checked) {
      const allIds = new Set(data.map(item => item.drop_entry_id));
      setSelectedIds(allIds);
    } else {
      setSelectedIds(new Set());
    }
  };

  const handleSelectItem = (cashDropId) => {
    const newSelected = new Set(selectedIds);
    if (newSelected.has(cashDropId)) {
      newSelected.delete(cashDropId);
    } else {
      newSelected.add(cashDropId);
    }
    setSelectedIds(newSelected);
  };

  const handleGetSummary = async () => {
    if (selectedIds.size === 0) {
      alert('Please select at least one cash drop');
      return;
    }

    setLoadingSummary(true);
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(API_ENDPOINTS.BANK_DROP_SUMMARY, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          cash_drop_ids: Array.from(selectedIds)
        })
      });

      if (response.ok) {
        const summary = await response.json();
        setSummaryData(summary);
        setShowSummaryModal(true);
      } else {
        const err = await response.json();
        alert(err.error || 'Failed to get summary');
      }
    } catch (error) {
      console.error('Error getting summary:', error);
      alert('Error getting summary');
    } finally {
      setLoadingSummary(false);
    }
  };

  const handleConfirmBankDrop = async () => {
    if (!summaryData) return;

    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(API_ENDPOINTS.BANK_DROP_MARK_DROPPED, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          cash_drop_ids: Array.from(selectedIds)
        })
      });

      if (response.ok) {
        const result = await response.json();
        alert(`Successfully marked ${result.updated_count} cash drop(s) as bank dropped`);
        setShowSummaryModal(false);
        setSelectedIds(new Set());
        setSummaryData(null);
        fetchData(); // Refresh data
      } else {
        const err = await response.json();
        alert(err.error || 'Failed to mark as bank dropped');
      }
    } catch (error) {
      console.error('Error confirming bank drop:', error);
      alert('Error confirming bank drop');
    }
  };

  const calculateTotal = (denominations) => {
    return DENOMINATION_CONFIG.reduce((total, denom) => {
      return total + (denominations[denom.field] || 0) * denom.value;
    }, 0);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8 font-sans text-gray-900">
      <div className="max-w-[1600px] mx-auto bg-white rounded-lg shadow-xl border border-gray-200 overflow-hidden">

        {/* HEADER & FILTERS */}
        <div className="p-6 border-b bg-white">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6">
            <div>
              <h2 className="text-2xl font-black uppercase italic tracking-tighter">Bank <span className="text-blue-600">Drop</span></h2>
              <p className="text-[10px] font-bold text-gray-400 tracking-widest uppercase">Reconciled Cash Drops for Bank Deposit</p>
            </div>

            <div className="flex flex-wrap items-end gap-4 bg-gray-50 p-4 rounded-lg border">
              {/* Custom Date Inputs */}
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-1">From</label>
                <input 
                  type="date" 
                  value={dateFrom} 
                  onChange={(e) => setDateFrom(e.target.value)} 
                  className="text-xs p-2 border rounded bg-white" 
                />
              </div>
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-1">To</label>
                <input 
                  type="date" 
                  value={dateTo} 
                  onChange={(e) => setDateTo(e.target.value)} 
                  className="text-xs p-2 border rounded bg-white" 
                />
              </div>

              <button 
                onClick={fetchData} 
                className="bg-gray-900 text-white px-4 py-2 rounded text-xs font-bold hover:bg-blue-600 transition"
              >
                Fetch
              </button>

              <div className="flex gap-1 border-l pl-4">
                <button 
                  onClick={() => handleQuickFilter('WTD')} 
                  className="px-3 py-2 bg-white border rounded text-[10px] font-black hover:bg-blue-50"
                >
                  WTD
                </button>
                <button 
                  onClick={() => handleQuickFilter('MTD')} 
                  className="px-3 py-2 bg-white border rounded text-[10px] font-black hover:bg-blue-50"
                >
                  MTD
                </button>
              </div>
            </div>
          </div>

          {/* Selection Controls */}
          {data.length > 0 && (
            <div className="mt-4 flex items-center gap-4 p-3 bg-blue-50 rounded-lg">
              <label className="flex items-center gap-2 text-sm font-bold">
                <input
                  type="checkbox"
                  checked={selectedIds.size === data.length && data.length > 0}
                  onChange={(e) => handleSelectAll(e.target.checked)}
                  className="w-4 h-4"
                />
                Select All ({selectedIds.size} selected)
              </label>
              <button
                onClick={handleGetSummary}
                disabled={selectedIds.size === 0 || loadingSummary}
                className="bg-green-600 text-white px-6 py-2 rounded text-sm font-bold hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                {loadingSummary ? 'Loading...' : `Get Summary (${selectedIds.size})`}
              </button>
            </div>
          )}
        </div>

        {/* DATA TABLE */}
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead>
              <tr className="bg-gray-50 text-[10px] font-black uppercase text-gray-500 border-b">
                <th className="p-4 w-12">
                  <input
                    type="checkbox"
                    checked={selectedIds.size === data.length && data.length > 0}
                    onChange={(e) => handleSelectAll(e.target.checked)}
                    className="w-4 h-4"
                  />
                </th>
                <th className="p-4">Date / Station / Shift</th>
                <th className="p-4">Reconciled Amount</th>
                <th className="p-4">WS Label</th>
                <th className="p-4">Variance</th>
                <th className="p-4">Bank Dropped</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.length > 0 ? data.map(item => (
                <tr key={item.id} className="border-b hover:bg-blue-50/30 transition-colors">
                  <td className="p-4">
                    <input
                      type="checkbox"
                      checked={selectedIds.has(item.drop_entry_id)}
                      onChange={() => handleSelectItem(item.drop_entry_id)}
                      className="w-4 h-4"
                    />
                  </td>
                  <td className="p-4">
                    <div className="text-xs font-black">{item.date}</div>
                    <div className="text-[10px] text-blue-600 font-bold uppercase">
                      {item.workstation} | Shift {item.shift_number}
                    </div>
                    <div className="text-[10px] text-gray-500">{item.user_name}</div>
                  </td>
                  <td className="p-4">
                    <button 
                      onClick={() => setSelectedImage(item.label_image_url)}
                      className="text-sm font-black text-gray-900 border-b-2 border-dotted border-blue-400 hover:text-blue-600"
                    >
                      ${item.admin_count_amount || item.system_drop_amount}
                    </button>
                    <div className="text-[9px] text-gray-400 uppercase mt-1">Click to view label</div>
                  </td>
                  <td className="p-4 text-sm font-bold text-gray-600">${item.ws_label_amount}</td>
                  <td className={`p-4 text-sm font-black ${parseFloat(item.variance) !== 0 ? 'text-red-500' : 'text-gray-300'}`}>
                    ${item.variance}
                  </td>
                  <td className="p-4">
                    <label className="flex items-center gap-2 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={item.bank_dropped || false}
                        onChange={() => handleBankDroppedToggle(item)}
                        className="w-5 h-5 text-blue-600 rounded focus:ring-blue-500"
                      />
                      <span className="text-xs font-bold">
                        {item.bank_dropped ? 'Yes' : 'No'}
                      </span>
                    </label>
                  </td>
                  <td className="p-4">
                    {item.bank_dropped && (
                      <button
                        onClick={() => handleBankDroppedToggle(item)}
                        className="text-xs text-blue-600 hover:text-blue-800 font-bold underline"
                      >
                        Edit Denominations
                      </button>
                    )}
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan="7" className="p-20 text-center text-gray-400 text-xs italic font-bold uppercase tracking-widest">
                    {loading ? "Loading Records..." : "No reconciled records found for this period"}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* MODAL FOR EDITING DENOMINATIONS */}
      {selectedCashDrop && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 p-4 backdrop-blur-sm">
          <div className="relative max-w-2xl w-full bg-white rounded-lg p-6 overflow-hidden shadow-2xl max-h-[90vh] overflow-y-auto">
            <button 
              onClick={() => {
                setSelectedCashDrop(null);
                setEditingDenominations({});
              }}
              className="absolute top-4 right-4 bg-red-600 text-white w-8 h-8 rounded-full font-bold flex items-center justify-center hover:bg-red-700 z-10"
            >

            </button>

            <h3 className="text-xl font-black mb-4">Edit Denominations - {selectedCashDrop.workstation} | {selectedCashDrop.date}</h3>

            <div className="grid grid-cols-2 gap-4 mb-6">
              {DENOMINATION_CONFIG.map(denom => (
                <div key={denom.field} className="flex items-center justify-between p-3 bg-gray-50 rounded border">
                  <label className="text-sm font-bold text-gray-700">{denom.name}</label>
                  <input
                    type="number"
                    min="0"
                    value={editingDenominations[denom.field] || 0}
                    onChange={(e) => setEditingDenominations({
                      ...editingDenominations,
                      [denom.field]: parseInt(e.target.value) || 0
                    })}
                    className="w-20 p-2 border rounded text-right font-bold"
                  />
                </div>
              ))}
            </div>

            <div className="mb-6 p-4 bg-blue-50 rounded border border-blue-200">
              <div className="flex justify-between items-center">
                <span className="text-sm font-bold text-gray-700">Total Amount:</span>
                <span className="text-2xl font-black text-blue-600">
                  ${calculateTotal(editingDenominations).toFixed(2)}
                </span>
              </div>
            </div>

            <div className="flex gap-4">
              <button
                onClick={handleSaveDenominations}
                className="flex-1 bg-green-600 text-white px-6 py-3 rounded-lg font-bold hover:bg-green-700"
              >
                Save & Mark as Bank Dropped
              </button>
              <button
                onClick={() => {
                  setSelectedCashDrop(null);
                  setEditingDenominations({});
                }}
                className="flex-1 bg-gray-500 text-white px-6 py-3 rounded-lg font-bold hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* SUMMARY MODAL */}
      {showSummaryModal && summaryData && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 p-4 backdrop-blur-sm">
          <div className="relative max-w-4xl w-full bg-white rounded-lg p-6 overflow-hidden shadow-2xl max-h-[90vh] overflow-y-auto">
            <button 
              onClick={() => {
                setShowSummaryModal(false);
                setSummaryData(null);
              }}
              className="absolute top-4 right-4 bg-red-600 text-white w-8 h-8 rounded-full font-bold flex items-center justify-center hover:bg-red-700 z-10"
            >

            </button>

            <h3 className="text-2xl font-black mb-4">Bank Drop Summary</h3>
            <p className="text-sm text-gray-600 mb-6">
              Total Cash Drops: {summaryData.count} | Total Amount: <span className="font-black text-green-600 text-xl">${summaryData.total_amount.toFixed(2)}</span>
            </p>

            <div className="mb-6">
              <h4 className="text-lg font-bold mb-3">Denomination Totals:</h4>
              <div className="grid grid-cols-3 gap-3">
                {DENOMINATION_CONFIG.map(denom => {
                  const count = summaryData.totals[denom.field] || 0;
                  const value = count * denom.value;
                  return count > 0 ? (
                    <div key={denom.field} className="p-3 bg-gray-50 rounded border flex justify-between items-center">
                      <span className="text-sm font-bold">{denom.name}:</span>
                      <div className="text-right">
                        <div className="font-black">{count}  ${denom.value.toFixed(2)}</div>
                        <div className="text-xs text-gray-600">= ${value.toFixed(2)}</div>
                      </div>
                    </div>
                  ) : null;
                })}
              </div>
            </div>

            <div className="mb-6 p-4 bg-green-50 rounded-lg border-2 border-green-500">
              <div className="flex justify-between items-center">
                <span className="text-lg font-bold text-gray-700">Grand Total:</span>
                <span className="text-3xl font-black text-green-600">
                  ${summaryData.total_amount.toFixed(2)}
                </span>
              </div>
            </div>

            <div className="flex gap-4">
              <button
                onClick={handleConfirmBankDrop}
                className="flex-1 bg-green-600 text-white px-6 py-3 rounded-lg font-bold hover:bg-green-700 text-lg"
              >
                Confirm Bank Drop
              </button>
              <button
                onClick={() => {
                  setShowSummaryModal(false);
                  setSummaryData(null);
                }}
                className="flex-1 bg-gray-500 text-white px-6 py-3 rounded-lg font-bold hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* MODAL FOR IMAGE */}
      {selectedImage && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 p-4 backdrop-blur-sm">
          <div className="relative max-w-4xl w-full bg-white rounded-lg p-2 overflow-hidden shadow-2xl">
            <button 
              onClick={() => setSelectedImage(null)}
              className="absolute top-4 right-4 bg-red-600 text-white w-8 h-8 rounded-full font-bold flex items-center justify-center hover:bg-red-700 z-10"
            >

            </button>
            <div className="max-h-[80vh] overflow-y-auto">
              <img 
                src={selectedImage || ''} 
                alt="WS Label" 
                className="w-full h-auto"
                onError={(e) => console.error("Image failed to load:", e.target.src)}
              />
            </div>
            <div className="p-4 bg-gray-50 text-center">
              <p className="text-xs font-black text-gray-500 uppercase tracking-tighter">Verified Workstation Receipt Label</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BankDrop;
