import React, { useState, useEffect } from 'react';

const CashDropValidation = () => {
  // --- State Management ---
  const [cashDrops, setCashDrops] = useState([]);
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // --- Mock Data Simulation ---
  // In a real app, you would fetch this from your API based on dateRange
  useEffect(() => {
    const mockData = Array.from({ length: 25 }, (_, i) => ({
      id: `CD-${1000 + i}`,
      date: '2026-01-12',
      systemAmount: (Math.random() * 500 + 100).toFixed(2),
      workstationAmount: '',
      reconciled: false,
    }));
    setCashDrops(mockData);
  }, [dateRange]);

  // --- Logic Functions ---

  // Update workstation input for a specific row
  const handleInputChange = (id, value) => {
    setCashDrops(prev => 
      prev.map(drop => drop.id === id ? { ...drop, workstationAmount: value } : drop)
    );
  };

  // Individual Reconcile
  const reconcileRow = (id) => {
    setCashDrops(prev => prev.map(drop => {
      if (drop.id === id) {
        if (parseFloat(drop.systemAmount) === parseFloat(drop.workstationAmount)) {
          return { ...drop, reconciled: true };
        } else {
          alert(`Mismatch for ${id}! Please verify amounts.`);
        }
      }
      return drop;
    }));
  };

  // Reconcile All (Current Page)
  const reconcileAllOnPage = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const currentItems = cashDrops.slice(startIndex, startIndex + itemsPerPage);

    setCashDrops(prev => prev.map(drop => {
      const isVisible = currentItems.find(item => item.id === drop.id);
      if (isVisible && !drop.reconciled && drop.workstationAmount) {
        if (parseFloat(drop.systemAmount) === parseFloat(drop.workstationAmount)) {
          return { ...drop, reconciled: true };
        }
      }
      return drop;
    }));
  };

  // --- Pagination Helpers ---
  const totalPages = Math.ceil(cashDrops.length / itemsPerPage);
  const currentData = cashDrops.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div className="p-8 bg-gray-50 min-h-screen font-sans text-gray-800">
      <div className="max-w-6xl mx-auto bg-white rounded-lg shadow-md p-6">

        {/* Header Section */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-6 gap-4">
          <div className="flex gap-4 items-center">
            <div>
              <label className="block text-xs font-bold uppercase text-gray-500">Start Date</label>
              <input 
                type="date" 
                className="border p-2 rounded" 
                onChange={(e) => setDateRange({ ...dateRange, start: e.target.value })}
              />
            </div>
            <div>
              <label className="block text-xs font-bold uppercase text-gray-500">End Date</label>
              <input 
                type="date" 
                className="border p-2 rounded"
                onChange={(e) => setDateRange({ ...dateRange, end: e.target.value })}
              />
            </div>
          </div>

          <button 
            onClick={reconcileAllOnPage}
            className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg font-semibold transition shadow-sm"
          >
            Reconcile All (This Page)
          </button>
        </div>

        {/* Data Table */}
        <div className="overflow-x-auto">
          <table className="w-full text-left border-collapse">
            <thead>
              <tr className="bg-gray-100 text-gray-600 uppercase text-sm">
                <th className="p-4 border-b">Drop ID</th>
                <th className="p-4 border-b">System Amt</th>
                <th className="p-4 border-b">Workstation Amt</th>
                <th className="p-4 border-b">Status</th>
                <th className="p-4 border-b text-center">Action</th>
              </tr>
            </thead>
            <tbody>
              {currentData.map((drop) => (
                <tr key={drop.id} className="hover:bg-gray-50 transition">
                  <td className="p-4 border-b font-medium">{drop.id}</td>
                  <td className="p-4 border-b text-blue-600 font-semibold">${drop.systemAmount}</td>
                  <td className="p-4 border-b">
                    <input 
                      type="number" 
                      placeholder="0.00"
                      disabled={drop.reconciled}
                      value={drop.workstationAmount}
                      onChange={(e) => handleInputChange(drop.id, e.target.value)}
                      className={`border p-2 rounded w-32 ${drop.reconciled ? 'bg-gray-100' : 'bg-white'}`}
                    />
                  </td>
                  <td className="p-4 border-b">
                    <span className={`px-3 py-1 rounded-full text-xs font-bold ${drop.reconciled ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                      {drop.reconciled ? 'True' : 'False'}
                    </span>
                  </td>
                  <td className="p-4 border-b text-center">
                    <button 
                      onClick={() => reconcileRow(drop.id)}
                      disabled={drop.reconciled || !drop.workstationAmount}
                      className={`px-4 py-1 rounded text-sm font-medium transition ${drop.reconciled ? 'text-gray-400 cursor-not-allowed' : 'text-white bg-blue-500 hover:bg-blue-600'}`}
                    >
                      Reconcile
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination Controls */}
        <div className="flex justify-between items-center mt-6">
          <p className="text-sm text-gray-500">
            Showing {currentData.length} of {cashDrops.length} drops
          </p>
          <div className="flex gap-2">
            <button 
              disabled={currentPage === 1}
              onClick={() => setCurrentPage(prev => prev - 1)}
              className="px-4 py-2 border rounded hover:bg-gray-100 disabled:opacity-50"
            >
              Previous
            </button>
            <span className="px-4 py-2 font-bold">{currentPage}</span>
            <button 
              disabled={currentPage === totalPages}
              onClick={() => setCurrentPage(prev => prev + 1)}
              className="px-4 py-2 border rounded hover:bg-gray-100 disabled:opacity-50"
            >
              Next
            </button>
          </div>
        </div>

      </div>
    </div>
  );
};

export default CashDropValidation;
