import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CdDashboard() {
  const navigate = useNavigate();
  const [selectedDateFrom, setSelectedDateFrom] = useState(new Date().toISOString().slice(0, 10));
  const [selectedDateTo, setSelectedDateTo] = useState(new Date().toISOString().slice(0, 10));
  const [cashDrops, setCashDrops] = useState([]);
  const [cashDrawers, setCashDrawers] = useState([]);
  const [activeDate, setActiveDate] = useState(null); 
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1. Initial User Fetch
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/auth/users/me/', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
          },
        });
        if (!response.ok) throw new Error('Failed to fetch user details');
        const userData = await response.json();
        setIsAdmin(userData.is_admin);
        setLoading(false);
      } catch (err) {
        navigate('/login');
      }
    };
    fetchUserDetails();
  }, [navigate]);

  // 2. Core Fetch Logic
  const fetchData = async (from, to) => {
    setError('');
    setActiveDate(null);
    try {
      const [dropResponse, drawerResponse] = await Promise.all([
        fetch(`http://localhost:8000/api/cash-drop-app1/cash-drop/?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
        fetch(`http://localhost:8000/api/cash-drop-app1/cash-drawer/?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
      ]);

      if (!dropResponse.ok || !drawerResponse.ok) throw new Error('Failed to fetch data from server');

      const dropData = await dropResponse.json();
      const drawerData = await drawerResponse.json();

      setCashDrops(dropData);
      setCashDrawers(drawerData);

      // Auto-select the first date if data exists
      const allDates = [...new Set([...dropData.map(d => d.date), ...drawerData.map(d => d.date)])].sort().reverse();
      if (allDates.length > 0) setActiveDate(allDates[0]);

    } catch (err) {
      setError(err.message);
    }
  };

  // 3. Date Range Helpers
  const handleWTD = () => {
    const now = new Date();
    const day = now.getDay();
    const diff = now.getDate() - day + (day === 0 ? -6 : 1); 
    const start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const handleMTD = () => {
    const start = new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const handleYTD = () => {
    const start = new Date(new Date().getFullYear(), 0, 1).toISOString().slice(0, 10);
    const end = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(start);
    setSelectedDateTo(end);
    fetchData(start, end);
  };

  const uniqueDates = [...new Set([...cashDrops.map(d => d.date), ...cashDrawers.map(d => d.date)])].sort().reverse();

  if (loading) return <div className="min-h-screen bg-gray-100 dark:bg-gray-900 flex items-center justify-center">Loading Dashboard...</div>;

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-950 p-4 font-sans">
      <div className="max-w-7xl mx-auto bg-white dark:bg-gray-900 rounded-3xl shadow-xl overflow-hidden border border-gray-100 dark:border-gray-800">

        {/* TOP BAR / NAVIGATION */}
        <div className="p-8 border-b border-gray-100 dark:border-gray-800">
          <h2 className="text-3xl font-black text-gray-800 dark:text-white mb-8 text-center tracking-tight">
            Cash Drop <span className="text-blue-600">Dashboard</span>
          </h2>

          <div className="flex flex-wrap items-end justify-center gap-6">
            <div className="flex gap-4">
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase ml-1 mb-1">From</label>
                <input type="date" value={selectedDateFrom} onChange={e => setSelectedDateFrom(e.target.value)} className="px-4 py-2 bg-gray-50 dark:bg-gray-800 border-none rounded-xl focus:ring-2 focus:ring-blue-500 dark:text-white" />
              </div>
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase ml-1 mb-1">To</label>
                <input type="date" value={selectedDateTo} onChange={e => setSelectedDateTo(e.target.value)} className="px-4 py-2 bg-gray-50 dark:bg-gray-800 border-none rounded-xl focus:ring-2 focus:ring-blue-500 dark:text-white" />
              </div>
            </div>

            <div className="flex gap-2">
              <button onClick={() => fetchData(selectedDateFrom, selectedDateTo)} className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white font-bold rounded-xl shadow-lg transition-all active:scale-95">Fetch</button>
              <div className="h-10 w-[1px] bg-gray-200 dark:bg-gray-700 mx-2"></div>
              <button onClick={handleWTD} className="px-4 py-2 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 text-gray-600 dark:text-gray-300 font-bold rounded-xl hover:bg-gray-50 transition-all">WTD</button>
              <button onClick={handleMTD} className="px-4 py-2 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 text-gray-600 dark:text-gray-300 font-bold rounded-xl hover:bg-gray-50 transition-all">MTD</button>
              <button onClick={handleYTD} className="px-4 py-2 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 text-gray-600 dark:text-gray-300 font-bold rounded-xl hover:bg-gray-50 transition-all">YTD</button>
            </div>
          </div>
        </div>

        {error && <div className="mx-8 mt-4 p-4 bg-red-50 text-red-600 rounded-2xl text-center font-bold border border-red-100">{error}</div>}

        <div className="flex flex-col md:flex-row min-h-[600px]">

          {/* SIDEBAR: DATE SELECTION */}
          <div className="w-full md:w-64 border-r border-gray-50 dark:border-gray-800 p-6 bg-gray-50/50 dark:bg-gray-900/50">
            <p className="text-[10px] font-black text-gray-400 uppercase mb-4 tracking-widest text-center">History Log</p>
            <div className="space-y-2 max-h-[500px] overflow-y-auto pr-2 custom-scrollbar">
              {uniqueDates.map(date => (
                <button
                  key={date}
                  onClick={() => setActiveDate(date)}
                  className={`w-full text-left px-4 py-3 rounded-2xl text-sm font-bold transition-all ${
                    activeDate === date 
                    ? 'bg-blue-600 text-white shadow-xl translate-x-1' 
                    : 'bg-white dark:bg-gray-800 text-gray-500 hover:bg-blue-50 border border-gray-100 dark:border-gray-700'
                  }`}
                >
                  {new Date(date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' })}
                </button>
              ))}
              {uniqueDates.length === 0 && <p className="text-center text-gray-400 text-xs py-10 italic">No records in range</p>}
            </div>
          </div>

          {/* MAIN CONTENT: SIDE-BY-SIDE TABLES */}
          <div className="flex-1 p-8">
            {!activeDate ? (
              <div className="h-full flex flex-col items-center justify-center opacity-30">
                <div className="w-20 h-20 bg-gray-200 rounded-full mb-4 animate-pulse"></div>
                <p className="font-bold text-gray-400">Select a date to view activity</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 animate-fadeIn">

                {/* COLUMN: CASH DROPS */}
                <div>
                  <div className="flex items-center gap-2 mb-6">
                    <div className="w-2 h-6 bg-blue-600 rounded-full"></div>
                    <h3 className="text-xl font-black text-gray-800 dark:text-gray-100 italic uppercase">Cash Drops</h3>
                  </div>

                  <div className="space-y-4">
                    {cashDrops.filter(d => d.date === activeDate).map(drop => (
                      <div key={drop.id} className="p-6 bg-white dark:bg-gray-800 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm">
                        <div className="flex justify-between items-center mb-4">
                          <span className="text-xs font-black bg-blue-50 text-blue-600 px-3 py-1 rounded-full uppercase">{drop.shift_number}</span>
                          <span className="text-2xl font-black text-blue-600">${drop.drop_amount}</span>
                        </div>
                        <p className="text-[10px] font-bold text-gray-400 uppercase mb-4">Station: {drop.workstation} â€¢ By: {drop.user_name}</p>

                        <div className="grid grid-cols-2 gap-2">
                          {Object.entries(drop).map(([key, value]) => (
                            ['hundreds', 'fifties', 'twenties', 'tens', 'fives', 'twos', 'ones', 'half_dollars', 'quarters', 'dimes', 'nickels', 'pennies'].includes(key) && value > 0 && (
                              <div key={key} className="flex justify-between items-center bg-gray-50 dark:bg-gray-900/50 px-3 py-2 rounded-xl">
                                <span className="text-[10px] text-gray-500 capitalize">{key.replace('_', ' ')}</span>
                                <span className="text-xs font-bold">{value}</span>
                              </div>
                            )
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* COLUMN: CASH DRAWERS */}
                <div>
                  <div className="flex items-center gap-2 mb-6">
                    <div className="w-2 h-6 bg-emerald-500 rounded-full"></div>
                    <h3 className="text-xl font-black text-gray-800 dark:text-gray-100 italic uppercase">Cash Drawers</h3>
                  </div>

                  <div className="space-y-4">
                    {cashDrawers.filter(d => d.date === activeDate).map(drawer => (
                      <div key={drawer.id} className="p-6 bg-white dark:bg-gray-800 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm">
                        <div className="flex justify-between items-center mb-2">
                          <span className="text-xs font-black bg-emerald-50 text-emerald-600 px-3 py-1 rounded-full uppercase">{drawer.workstation}</span>
                          <span className="text-xs font-bold text-gray-400">Initial: ${drawer.starting_cash}</span>
                        </div>
                        <p className="text-2xl font-black text-emerald-600 mb-4">${drawer.total_cash}</p>

                        <div className="grid grid-cols-2 gap-2">
                          {Object.entries(drawer).map(([key, value]) => (
                            ['hundreds', 'fifties', 'twenties', 'tens', 'fives', 'twos', 'ones', 'half_dollars', 'quarters', 'dimes', 'nickels', 'pennies'].includes(key) && value > 0 && (
                              <div key={key} className="flex justify-between items-center bg-gray-50 dark:bg-gray-900/50 px-3 py-2 rounded-xl">
                                <span className="text-[10px] text-gray-500 capitalize">{key.replace('_', ' ')}</span>
                                <span className="text-xs font-bold">{value}</span>
                              </div>
                            )
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

              </div>
            )}
          </div>
        </div>
      </div>

      {/* STYLE INJECTION FOR SIDEBAR SCROLLBAR */}
      <style>{`
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-track { background: transparent; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #e2e8f0; border-radius: 10px; }
        .dark .custom-scrollbar::-webkit-scrollbar-thumb { background: #334155; }
        @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
        .animate-fadeIn { animation: fadeIn 0.4s ease forwards; }
      `}</style>
    </div>
  );
}

export default CdDashboard;
