import React, { useState } from 'react';

const CashDropReconcilerPage = () => {
  const [dateRange, setDateRange] = useState({ datefrom: '', dateto: '' });
  const [reconciliations, setReconciliations] = useState([]);
  const [loading, setLoading] = useState(false);

  // Helper for Headers
  const getHeaders = () => ({
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${localStorage.getItem('access_token')}`
  });

  // Fetch Data: GET
  const handleSearch = async () => {
    // 1. Validation check
    if (!dateRange.datefrom || !dateRange.dateto) {
      return alert("Please select both a 'From' and 'To' date.");
    }

    setLoading(true);
    try {
      // 2. Ensure keys match the backend: datefrom and dateto
      const params = new URLSearchParams({
        datefrom: dateRange.datefrom,
        dateto: dateRange.dateto
      });

      // 3. Use the absolute URL with the trailing slash
      const url = `http://localhost:8000/api/cash-drop-app1/cash-drop-reconciler/?${params.toString()}`;

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}` // Matches CashDrop.js key
        },
      });

      // 4. Detailed error logging
      if (response.ok) {
        const data = await response.json();
        console.log("Fetched Data:", data); // Check your browser console!
        setReconciliations(data);
      } else {
        const errorText = await response.text();
        console.error("Server Error Response:", errorText);
        alert(`Fetch failed: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error("Network/Fetch Error:", error);
      alert("Connection refused. Is the Django server running?");
    } finally {
      setLoading(false);
    }
  };
  // Update Record: PATCH
  const reconcileRecord = async (item) => {
    try {
      const response = await fetch(`http://localhost:8000/api/cash-drop-app1/cash-drop-reconciler/`, {
        method: 'PATCH',
        headers: getHeaders(),
        body: JSON.stringify({
          id: item.id,
          wsamount: item.wsamount,
          is_reconciled: item.wsamount == item.drop_amount ? true : false
        }),
      });

      const updatedItem = await response.json();
      if (response.ok) {
        setReconciliations(prev => 
          prev.map(i => (i.id === item.id ? updatedItem : i))
        );
      } else {
        alert("Validation failed: Check the amounts.");
      }
    } catch (error) {
      console.error("Update error:", error);
    }
  };

  const handleInputChange = (id, value) => {
    setReconciliations(prev => 
      prev.map(i => i.id === id ? { ...i, wsamount: value } : i)
    );
  };

  return (
    <div className="p-8 bg-gray-50 min-h-screen font-sans">
      <div className="max-w-6xl mx-auto bg-white rounded-xl shadow-sm border border-gray-200 p-6">

        {/* Header & Controls */}
        <div className="flex flex-col md:flex-row justify-between items-end mb-8 gap-4">
          <div className="flex gap-4">
            <div className="flex flex-col">
              <span className="text-xs font-bold text-gray-400 uppercase mb-1">From</span>
              <input type="date" className="border rounded-md p-2" onChange={e => setDateRange({...dateRange, datefrom: e.target.value})} />
            </div>
            <div className="flex flex-col">
              <span className="text-xs font-bold text-gray-400 uppercase mb-1">To</span>
              <input type="date" className="border rounded-md p-2" onChange={e => setDateRange({...dateRange, dateto: e.target.value})} />
            </div>
            <button 
              onClick={handleSearch}
              className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-md self-end transition"
            >
              Search
            </button>
          </div>

          <button 
            onClick={() => reconciliations.filter(i => !i.is_reconciled).forEach(reconcileRecord)}
            className="bg-emerald-600 hover:bg-emerald-700 text-white px-5 py-2 rounded-md font-semibold transition"
          >
            Reconcile All
          </button>
        </div>

        {/* Results Table */}
        <div className="overflow-hidden rounded-lg border border-gray-100">
          <table className="w-full text-left border-collapse">
            <thead className="bg-gray-50 text-gray-500 text-xs uppercase">
              <tr>
                <th className="p-4">WS / Shift</th>
                <th className="p-4">User</th>
                <th className="p-4">Date</th>
                <th className="p-4">Drop Amount</th>
                <th className="p-4">WS Label</th>
                <th className="p-4">WS Amount</th>
                <th className="p-4 text-center">Reconciled</th>
                <th className="p-4 text-right">Action</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-100">
              {reconciliations.map((item) => (
                <tr key={item.id} className="hover:bg-gray-50 transition-colors text-sm">
                  <td className="p-4 font-medium">{item.workstation} / {item.shift_number}</td>
                  <td className="p-4 text-gray-500">{item.user_name}</td>
                  <td className="p-4">{item.date}</td>
                  <td className="p-4">{item.drop_amount_display}</td>
                  <td className="p-4">
                    <input type='file'/>
                  </td>
                  <td className="p-4">
                    <input 
                      type="number" 
                      className="w-28 border rounded p-1.5 focus:ring-2 focus:ring-blue-100 outline-none"
                      value={item.wsamount}
                      disabled={item.is_reconciled}
                      onChange={(e) => handleInputChange(item.id, e.target.value)}
                    />
                  </td>
                  <td className="p-4 text-center">
                    <span className={`px-2.5 py-0.5 rounded-full text-xs font-bold ${item.is_reconciled ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-600'}`}>
                      {item.is_reconciled ? 'True' : 'False'}
                    </span>
                  </td>
                  <td className="p-4 text-right">
                    <button 
                      onClick={() => reconcileRecord(item)}
                      disabled={item.is_reconciled || !item.wsamount}
                      className={`px-4 py-1.5 rounded-md font-bold transition ${item.is_reconciled ? 'text-gray-300' : 'text-blue-600 hover:bg-blue-50'}`}
                    >
                      Reconcile
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {reconciliations.length === 0 && !loading && (
            <div className="p-10 text-center text-gray-400 italic">No records to display.</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CashDropReconcilerPage;
