import React, { useState, useEffect } from 'react';

function CashDropValidation() {
  const [data, setData] = useState([]);
  const [filterDate, setFilterDate] = useState(new Date().toISOString().slice(0, 10));
  const [showOnlyUnreconciled, setShowOnlyUnreconciled] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [adminCounts, setAdminCounts] = useState({});

  const fetchData = async () => {
    const token = localStorage.getItem('access_token');
    const response = await fetch(`http://localhost:8000/api/cash-drop-app1/cash-drop-reconciler/?date=${filterDate}`, {
      headers: { 'Authorization': `Bearer ${token}` }
    });
    const result = await response.json();
    setData(result);
  };

  useEffect(() => { fetchData(); }, [filterDate]);

  const handleReconcile = async (item) => {
    const count = adminCounts[item.id];
    if (parseFloat(count) !== parseFloat(item.drop_amount)) {
      alert(`Mismatch! Counted: $${count} vs System: $${item.drop_amount}`);
      return;
    }

    const response = await fetch(`http://localhost:8000/api/cash-drop-app1/reconcile/${item.id}/`, {
      method: 'PATCH',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      },
      body: JSON.stringify({ admin_count_amount: count })
    });

    if (response.ok) fetchData();
  };

  const setRange = (type) => {
    const now = new Date();
    let start;
    if (type === 'WTD') start = new Date(now.setDate(now.getDate() - now.getDay()));
    if (type === 'MTD') start = new Date(now.getFullYear(), now.getMonth(), 1);
    setFilterDate(start.toISOString().slice(0, 10));
  };

  const filteredList = showOnlyUnreconciled ? data.filter(d => !d.is_reconciled) : data;

  return (
    <div className="p-8 bg-gray-50 min-h-screen font-sans text-gray-800">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-sm border border-gray-200">

        {/* Header & Filters */}
        <div className="p-6 border-b flex flex-wrap justify-between items-center gap-4">
          <h2 className="text-xl font-black uppercase italic tracking-tighter">Validation <span className="text-blue-600">Center</span></h2>

          <div className="flex items-center gap-4">
            <div className="flex bg-gray-100 p-1 rounded-md">
              <button onClick={() => setRange('WTD')} className="px-3 py-1 text-[10px] font-bold hover:bg-white rounded transition">WTD</button>
              <button onClick={() => setRange('MTD')} className="px-3 py-1 text-[10px] font-bold hover:bg-white rounded transition">MTD</button>
            </div>

            <div className="flex items-center gap-2 border-l pl-4">
              <span className="text-[10px] font-bold text-gray-400 uppercase">Show All</span>
              <button 
                onClick={() => setShowOnlyUnreconciled(!showOnlyUnreconciled)}
                className={`w-10 h-5 rounded-full relative transition-colors ${showOnlyUnreconciled ? 'bg-blue-600' : 'bg-gray-300'}`}
              >
                <div className={`absolute top-1 w-3 h-3 bg-white rounded-full transition-all ${showOnlyUnreconciled ? 'left-6' : 'left-1'}`} />
              </button>
              <span className="text-[10px] font-bold text-gray-400 uppercase">Pending Only</span>
            </div>
          </div>
        </div>

        {/* Data Table */}
        <table className="w-full text-left border-collapse">
          <thead>
            <tr className="bg-gray-50 text-[10px] font-black uppercase text-gray-400 border-b">
              <th className="p-4">Station/User</th>
              <th className="p-4">Drop Amount</th>
              <th className="p-4">WS Amount</th>
              <th className="p-4">Variance</th>
              <th className="p-4">Amount Counted</th>
              <th className="p-4">Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredList.map(item => (
              <tr key={item.id} className="border-b hover:bg-gray-50/50 transition">
                <td className="p-4">
                  <div className="font-bold text-sm">{item.workstation}</div>
                  <div className="text-[10px] text-gray-400">{item.user_name}</div>
                </td>
                <td className="p-4">
                  <button 
                    onClick={() => setSelectedImage(item.label_image_url)}
                    className="text-blue-600 font-black text-sm hover:underline"
                  >
                    ${item.drop_amount}
                  </button>
                </td>
                <td className="p-4 text-sm font-medium text-gray-600">${item.ws_amount}</td>
                <td className={`p-4 text-sm font-bold ${parseFloat(item.variance) !== 0 ? 'text-red-500' : 'text-gray-400'}`}>
                  ${item.variance}
                </td>
                <td className="p-4">
                  {item.is_reconciled ? (
                    <span className="text-sm font-bold">${item.admin_count_amount}</span>
                  ) : (
                    <input 
                      type="number" 
                      placeholder="Enter count"
                      className="w-24 p-1 border rounded text-sm focus:ring-1 focus:ring-blue-500 outline-none"
                      onChange={(e) => setAdminCounts({...adminCounts, [item.id]: e.target.value})}
                    />
                  )}
                </td>
                <td className="p-4">
                  {item.is_reconciled ? (
                    <span className="bg-green-100 text-green-700 text-[10px] font-black px-2 py-1 rounded">RECONCILED</span>
                  ) : (
                    <button 
                      disabled={!adminCounts[item.id]}
                      onClick={() => handleReconcile(item)}
                      className="bg-blue-600 text-white text-[10px] font-black px-3 py-1 rounded hover:bg-blue-700 disabled:opacity-30 transition"
                    >
                      RECONCILE
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* IMAGE MODAL */}
      {selectedImage && (
        <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <div className="bg-white p-2 rounded-lg max-w-2xl w-full relative">
            <button 
              onClick={() => setSelectedImage(null)}
              className="absolute -top-10 right-0 text-white font-bold hover:text-red-400"
            >
              CLOSE [X]
            </button>
            <img 
              src={`http://localhost:8000${selectedImage}`} 
              alt="Workstation Label" 
              className="w-full rounded border shadow-inner"
            />
            <div className="p-4 text-center text-[10px] font-bold text-gray-400 uppercase tracking-widest">
              Verified Workstation Label Upload
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CashDropValidation;
