import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_ENDPOINTS } from '../config/api';

function CashDrop() {
  const navigate = useNavigate();

  // --- STATE ---
  const [formData, setFormData] = useState({
    employeeName: '',
    shiftNumber: '',
    workStation: '',
    date: new Date().toISOString().slice(0, 10),
    startingCash: '200.00',
    wsLabelAmount: 0,
    pennies: 0, nickels: 0, dimes: 0, quarters: 0, halfDollars: 0,
    ones: 0, twos: 0, fives: 0, tens: 0, twenties: 0, fifties: 0, hundreds: 0,
  });

  const [labelImage, setLabelImage] = useState(null);
  const [cashDropDenominations, setCashDropDenominations] = useState(null);
  const [remainingCashInDrawer, setRemainingCashInDrawer] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  // Modal State for Popups
  const [modal, setModal] = useState({ show: false, title: '', message: '', type: 'info' });

  const DENOMINATION_CONFIG = [
    { name: 'Hundreds', value: 100, field: 'hundreds' },
    { name: 'Fifties', value: 50, field: 'fifties' },
    { name: 'Twenties', value: 20, field: 'twenties' },
    { name: 'Tens', value: 10, field: 'tens' },
    { name: 'Fives', value: 5, field: 'fives' },
    { name: 'Twos', value: 2, field: 'twos' },
    { name: 'Ones', value: 1, field: 'ones' },
    { name: 'Half Dollars', value: 0.50, field: 'halfDollars' },
    { name: 'Quarters', value: 0.25, field: 'quarters' },
    { name: 'Dimes', value: 0.10, field: 'dimes' },
    { name: 'Nickels', value: 0.05, field: 'nickels' },
    { name: 'Pennies', value: 0.01, field: 'pennies' },
  ];

  // --- EFFECTS ---
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(API_ENDPOINTS.CURRENT_USER, {
          headers: { 'Authorization': `Bearer ${sessionStorage.getItem('access_token')}` },
        });
        if (!response.ok) throw new Error('Auth failed');
        const data = await response.json();
        setFormData(prev => ({ ...prev, employeeName: data.name }));
        setIsAdmin(data.is_admin);
        setLoading(false);
      } catch (err) { navigate('/login'); }
    };
    fetchUser();
  }, [navigate]);

  useEffect(() => {
    calculateDenominations();
  }, [formData]);

  // --- HELPERS ---
  const showAlert = (title, message, type = 'error') => {
    setModal({ show: true, title, message, type });
  };

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'number' || name === 'wsLabelAmount' ? parseFloat(value) || 0 : value,
    }));
  };

  const calculateTotalCash = () => DENOMINATION_CONFIG.reduce((acc, d) => acc + (formData[d.field] * d.value), 0).toFixed(2);
  const calculateDropAmount = () => (parseFloat(calculateTotalCash()) - parseFloat(formData.startingCash)).toFixed(2);
  const calculateVariance = () => (parseFloat(calculateDropAmount()) - formData.wsLabelAmount).toFixed(2);

  const calculateDenominations = () => {
    const amountToDrop = parseFloat(calculateDropAmount());
    if (amountToDrop <= 0) { setCashDropDenominations(null); return; }

    let remaining = Math.round(amountToDrop * 100);
    const dropBreakdown = {};
    const finalDrawer = {};

    DENOMINATION_CONFIG.forEach(denom => {
      const valCents = Math.round(denom.value * 100);
      const available = formData[denom.field];
      let count = 0;
      if (valCents > 0 && remaining >= valCents) {
        count = Math.min(Math.floor(remaining / valCents), available);
        remaining -= count * valCents;
      }
      dropBreakdown[denom.field] = count;
      finalDrawer[denom.field] = available - count;
    });

    setCashDropDenominations(dropBreakdown);
    setRemainingCashInDrawer(finalDrawer);
  };

  const isSubmitValid = () => {
    const drop = parseFloat(calculateDropAmount());
    const mathCheck = Math.abs(drop - (parseFloat(calculateTotalCash()) - parseFloat(formData.startingCash))) < 0.01;
    return mathCheck && drop > 0 && formData.workStation && labelImage;
  };

  const handleSubmit = async () => {
    const token = sessionStorage.getItem('access_token');
    try {
      // 1. Save Drawer
      const drawerData = {
        workstation: formData.workStation,
        shift_number: formData.shiftNumber,
        date: formData.date,
        starting_cash: parseFloat(formData.startingCash),
        total_cash: parseFloat(calculateTotalCash()),
        ...Object.fromEntries(DENOMINATION_CONFIG.map(d => [d.field === 'halfDollars' ? 'half_dollars' : d.field, formData[d.field]]))
      };

      const dRes = await fetch(API_ENDPOINTS.CASH_DRAWER, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
        body: JSON.stringify(drawerData),
      });

      if (!dRes.ok) throw new Error('Failed to save Drawer data.');
      const dResult = await dRes.json();

      // 2. Save Drop with Image
      const dropForm = new FormData();
      dropForm.append('drawer_entry', dResult.id);
      dropForm.append('workstation', formData.workStation);
      dropForm.append('shift_number', formData.shiftNumber);
      dropForm.append('date', formData.date);
      dropForm.append('drop_amount', calculateDropAmount());
      dropForm.append('ws_label_amount', formData.wsLabelAmount);
      dropForm.append('variance', calculateVariance());
      if (labelImage) dropForm.append('label_image', labelImage);

      Object.keys(cashDropDenominations).forEach(key => {
        const backendKey = key === 'halfDollars' ? 'half_dollars' : key;
        dropForm.append(backendKey, cashDropDenominations[key]);
      });

      const dropRes = await fetch(API_ENDPOINTS.CASH_DROP, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` },
        body: dropForm,
      });

      if (!dropRes.ok) throw new Error('Failed to save Cash Drop & Image.');

      showAlert("Success", "Cash Drop and Label Image have been stored successfully.", "success");
      setTimeout(() => navigate('/cd-dashboard'), 2000);

    } catch (err) {
      showAlert("Submission Error", err.message, "error");
    }
  };

  if (loading) return <div className="h-screen flex items-center justify-center dark:bg-gray-900 dark:text-white">Initializing Terminal...</div>;

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-950 p-6 font-sans">

      {/* CUSTOM MODAL COMPONENT */}
      {modal.show && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm p-4">
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-2xl max-w-sm w-full p-6 border-t-4 border-blue-500 animate-fadeIn">
            <h3 className={`text-lg font-black mb-2 ${modal.type === 'error' ? 'text-red-600' : 'text-green-600'}`}>{modal.title}</h3>
            <p className="text-gray-600 dark:text-gray-300 text-sm mb-6">{modal.message}</p>
            <button onClick={() => setModal({ ...modal, show: false })} className="w-full py-2 bg-gray-900 dark:bg-blue-600 text-white font-bold rounded hover:opacity-90 transition-all">Close</button>
          </div>
        </div>
      )}

      <div className="max-w-7xl mx-auto bg-white dark:bg-gray-900 rounded-lg shadow-sm border border-gray-200 dark:border-gray-800 overflow-hidden">
        <div className="bg-pink-600 p-4"><h2 className="text-white font-black italic tracking-widest text-center uppercase">CashDrop Terminal</h2></div>

        <div className="p-8">
          {/* Section 1: Top Bar Details */}
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4 mb-8 p-4 bg-gray-50 dark:bg-gray-800 rounded-lg">
            <div className="flex flex-col"><label className="text-[10px] font-bold text-gray-400 uppercase">Employee</label><input type="text" value={formData.employeeName} readOnly className="p-2 text-sm bg-transparent border-b font-bold" /></div>
            <div className="flex flex-col"><label className="text-[10px] font-bold text-gray-400 uppercase">Shift</label><input type="text" name="shiftNumber" onChange={handleChange} className="p-2 text-sm bg-transparent border-b dark:text-white" /></div>
            <div className="flex flex-col"><label className="text-[10px] font-bold text-gray-400 uppercase">Station</label><input type="text" name="workStation" onChange={handleChange} className="p-2 text-sm bg-transparent border-b dark:text-white" /></div>
            <div className="flex flex-col"><label className="text-[10px] font-bold text-gray-400 uppercase">Date</label><input type="date" name="date" value={formData.date} onChange={handleChange} className="p-2 text-sm bg-transparent border-b dark:text-white" /></div>
            <div className="flex flex-col"><label className="text-[10px] font-bold text-gray-400 uppercase">Starting Cash</label><input type="number" name="startingCash" value={formData.startingCash} readOnly={!isAdmin} onChange={handleChange} className="p-2 text-sm bg-transparent border-b font-bold text-blue-600" /></div>
            <div className="flex flex-col"><label className="text-[10px] font-bold text-pink-600 uppercase">Cash Received on Receipt</label><input type="number" name="wsLabelAmount" onChange={handleChange} className="p-2 text-sm bg-transparent border-b font-bold text-pink-600 focus:border-pink-600 outline-none" /></div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 items-start">
            {/* Input Column */}
            <div className="bg-white dark:bg-gray-800 border rounded-lg p-6">
              <h3 className="text-xs font-black uppercase text-gray-400 mb-6 tracking-widest border-b pb-2">1. Drawer Inventory</h3>
              <div className="space-y-3">
                {DENOMINATION_CONFIG.map(d => (
                  <div key={d.field} className="flex justify-between items-center">
                    <span className="text-xs font-bold text-gray-500">{d.name + ' ( $' + d.value + 's)'}</span>
                    <input type="number" name={d.field} value={formData[d.field]} onChange={handleChange} className="w-20 p-1 border rounded text-right text-sm dark:bg-gray-700" min="0" />
                  </div>
                ))}
              </div>
              <div className="mt-8 pt-4 border-t space-y-2">
                <div className="flex justify-between text-xs"><span>Drawer Total:</span> <span className="font-bold">${calculateTotalCash()}</span></div>
              </div>
            </div>

            {/* Auto Drop Column */}
            <div className="bg-blue-50/50 dark:bg-gray-800/50 border border-blue-100 dark:border-gray-700 rounded-lg p-6">
              <h3 className="text-xs font-black uppercase text-blue-400 mb-6 tracking-widest border-b border-blue-100 pb-2">2. Suggested Cash Drop</h3>
              <div className="space-y-2">
                {cashDropDenominations ? DENOMINATION_CONFIG.map(d => cashDropDenominations[d.field] > 0 && (
                  <div key={d.field} className="flex justify-between p-2 bg-white dark:bg-gray-800 rounded border border-blue-50">
                    <span className="text-[10px] font-bold uppercase text-gray-400">{d.name + '(' + d.value + ')'}</span>
                    <span className="text-sm font-black text-blue-600">x {cashDropDenominations[d.field]}</span>
                  </div>
                )) : <p className="text-center text-gray-300 text-xs py-20 italic">Enter amounts to calculate...</p>}
              <div className="flex justify-between text-xs text-green-600"><span>Net Drop:</span> <span className="font-black">${calculateDropAmount()}</span></div>
              <div className="flex justify-between text-xs text-pink-600"><span>Variance:</span> <span className="font-black">${calculateVariance()}</span></div>
              </div>
            </div>

            {/* Image & Submit Column */}
            <div className="space-y-6">
              <div className="bg-white dark:bg-gray-800 border rounded-lg p-6">
                <h3 className="text-xs font-black uppercase text-gray-400 mb-6 tracking-widest border-b pb-2">3. Cash Drop Receipt</h3>
                <label className={`group flex flex-col items-center justify-center w-full h-40 border-2 border-dashed rounded-lg cursor-pointer transition-all ${labelImage ? 'border-green-500 bg-green-50' : 'border-gray-300 hover:border-pink-500'}`}>
                  <div className="flex flex-col items-center justify-center pt-5 pb-6">
                    <p className="text-sm font-bold text-gray-500">{labelImage ? "✅ Image Ready" : "Upload Cash Drop Receipt"}</p>
                    <p className="text-[10px] text-gray-400 mt-1">{labelImage ? labelImage.name : "PNG, JPG or JPEG"}</p>
                  </div>
                  <input type="file" className="hidden" onChange={(e) => setLabelImage(e.target.files[0])} accept="image/*" />
                </label>
              </div>

              {isSubmitValid() ? (
                <button onClick={handleSubmit} className="w-full py-4 bg-blue-600 hover:bg-blue-700 text-white font-black rounded-lg shadow-lg transform transition active:scale-95 uppercase tracking-widest">
                  Finalize Cash Drop
                </button>
              ) : (
                <div className="p-4 bg-red-50 dark:bg-red-900/20 border border-red-100 dark:border-red-800 rounded-lg">
                  <p className="text-[10px] font-black text-red-500 uppercase leading-relaxed">
                    {!labelImage && "• Missing Workstation Label Image"}<br/>
                    {parseFloat(calculateDropAmount()) <= 0 && "• Drop Amount must be positive"}
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <style>{`
        @keyframes fadeIn { from { opacity: 0; transform: scale(0.95); } to { opacity: 1; transform: scale(1); } }
        .animate-fadeIn { animation: fadeIn 0.2s ease-out forwards; }
      `}</style>
    </div>
  );
}

export default CashDrop;
