import React, { useState } from 'react';
import axios from 'axios';

const CashDropReconcilerPage = () => {
    const [dateRange, setDateRange] = useState({ datefrom: '', dateto: '' });
    const [reconciliations, setReconciliations] = useState([]);
    const [loading, setLoading] = useState(false);

    // Fetch Data from Backend
    const fetchDrops = async () => {
        if (!dateRange.datefrom || !dateRange.dateto) {
            alert("Please select both dates.");
            return;
        }
        setLoading(true);
        try {
            const token = localStorage.getItem('token'); // Assuming JWT storage
            const response = await axios.get(`/api/cash-drop-reconciler/`, {
                params: dateRange,
                headers: { Authorization: `Bearer ${token}` }
            });
            setReconciliations(response.data);
        } catch (err) {
            alert(err.response?.data?.error || "Failed to fetch data");
        } finally {
            setLoading(false);
        }
    };

    // Update amount in local state
    const handleAmountChange = (id, val) => {
        setReconciliations(prev => prev.map(item => 
            item.id === id ? { ...item, wsamount: val } : item
        ));
    };

    // Reconcile Single (PATCH request)
    const handleReconcile = async (item) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.patch(`/api/cash-drop-reconciler/`, {
                id: item.id,
                wsamount: item.wsamount,
                is_reconciled: true
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            // Update local state to reflect change
            setReconciliations(prev => prev.map(i => i.id === item.id ? response.data : i));
        } catch (err) {
            alert("Reconciliation failed. Ensure amount is valid.");
        }
    };

    // Reconcile All (Loops through local list)
    const handleReconcileAll = async () => {
        const unreconciled = reconciliations.filter(i => !i.is_reconciled && i.wsamount > 0);
        for (const item of unreconciled) {
            await handleReconcile(item);
        }
    };

    return (
        <div className="p-6 bg-gray-50 min-h-screen">
            <div className="max-w-6xl mx-auto bg-white shadow rounded-lg p-6">
                <h1 className="text-2xl font-bold mb-6 text-gray-800">Cash Drop Validation</h1>

                {/* Filters */}
                <div className="flex flex-wrap items-end gap-4 mb-8 border-b pb-6">
                    <div className="flex flex-col">
                        <label className="text-sm font-medium text-gray-600">Date From</label>
                        <input type="date" className="border rounded p-2" onChange={e => setDateRange({...dateRange, datefrom: e.target.value})} />
                    </div>
                    <div className="flex flex-col">
                        <label className="text-sm font-medium text-gray-600">Date To</label>
                        <input type="date" className="border rounded p-2" onChange={e => setDateRange({...dateRange, dateto: e.target.value})} />
                    </div>
                    <button onClick={fetchDrops} className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">Search</button>
                    <button onClick={handleReconcileAll} className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 ml-auto">Reconcile All</button>
                </div>

                {/* Table */}
                <div className="overflow-x-auto">
                    <table className="w-full text-left border-collapse">
                        <thead>
                            <tr className="bg-gray-100 border-b">
                                <th className="p-3 text-sm font-semibold">WS / Shift</th>
                                <th className="p-3 text-sm font-semibold">User</th>
                                <th className="p-3 text-sm font-semibold">Date</th>
                                <th className="p-3 text-sm font-semibold">Workstation Amount</th>
                                <th className="p-3 text-sm font-semibold">Status</th>
                                <th className="p-3 text-sm font-semibold">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reconciliations.map(item => (
                                <tr key={item.id} className="border-b hover:bg-gray-50">
                                    <td className="p-3">{item.workstation} / {item.shift_number}</td>
                                    <td className="p-3 text-gray-600">{item.user_name}</td>
                                    <td className="p-3">{item.date}</td>
                                    <td className="p-3">
                                        <input 
                                            type="number" 
                                            className="border rounded p-1 w-24"
                                            value={item.wsamount}
                                            disabled={item.is_reconciled}
                                            onChange={e => handleAmountChange(item.id, e.target.value)}
                                        />
                                    </td>
                                    <td className="p-3">
                                        <span className={`px-2 py-1 rounded-full text-xs ${item.is_reconciled ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                                            {item.is_reconciled ? 'True' : 'False'}
                                        </span>
                                    </td>
                                    <td className="p-3">
                                        <button 
                                            disabled={item.is_reconciled}
                                            onClick={() => handleReconcile(item)}
                                            className={`px-3 py-1 rounded text-sm ${item.is_reconciled ? 'bg-gray-200 text-gray-400' : 'bg-blue-100 text-blue-700 hover:bg-blue-200'}`}
                                        >
                                            Reconcile
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    {!loading && reconciliations.length === 0 && (
                        <p className="text-center py-10 text-gray-400">No data found for selected dates.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default CashDropReconcilerPage;
