import React, { useState, useEffect } from 'react';
import { API_ENDPOINTS } from '../config/api';

function CashDropValidation() {
  const [data, setData] = useState([]);
  const [dateFrom, setDateFrom] = useState(new Date().toISOString().slice(0, 10));
  const [dateTo, setDateTo] = useState(new Date().toISOString().slice(0, 10));
  const [showOnlyUnreconciled, setShowOnlyUnreconciled] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [adminCounts, setAdminCounts] = useState({});
  const [loading, setLoading] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    const token = localStorage.getItem('access_token');
    try {
      const response = await fetch(
        `${API_ENDPOINTS.CASH_DROP_RECONCILER}?datefrom=${dateFrom}&dateto=${dateTo}`,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      const result = await response.json();
      setData(Array.isArray(result) ? result : []);
    } catch (err) {
      console.error("Fetch error:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [showOnlyUnreconciled]); // Refetch or re-filter when toggle changes if desired

  const handleReconcile = async (item) => {
    const count = adminCounts[item.id];

    // Frontend Check
    if (parseFloat(count) !== parseFloat(item.system_drop_amount)) {
      alert(`Math Mismatch! Counted: $${count} | System: $${item.system_drop_amount}. They must be identical.`);
      return;
    }

    const response = await fetch(API_ENDPOINTS.CASH_DROP_RECONCILER, {
      method: 'PATCH',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      },
      body: JSON.stringify({ 
        id: item.id, 
        admin_count_amount: count 
      })
    });

    if (response.ok) {
      alert("Record Reconciled successfully.");
      fetchData();
    } else {
      const err = await response.json();
      alert(err.error || "Failed to reconcile");
    }
  };

  const handleQuickFilter = (type) => {
    const now = new Date();
    let start = new Date();
    if (type === 'WTD') {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff));
    } else if (type === 'MTD') {
      start = new Date(now.getFullYear(), now.getMonth(), 1);
    }
    setDateFrom(start.toISOString().slice(0, 10));
    setDateTo(new Date().toISOString().slice(0, 10));
  };

  const filteredList = showOnlyUnreconciled ? data.filter(d => !d.is_reconciled) : data;

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8 font-sans text-gray-900">
      <div className="max-w-[1400px] mx-auto bg-white rounded-lg shadow-xl border border-gray-200 overflow-hidden">

        {/* HEADER & FILTERS */}
        <div className="p-6 border-b bg-white">
          <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-6">
            <div>
              <h2 className="text-2xl font-black uppercase italic tracking-tighter">Validation <span className="text-blue-600">Terminal</span></h2>
              <p className="text-[10px] font-bold text-gray-400 tracking-widest uppercase">Audit & Reconciliation</p>
            </div>

            <div className="flex flex-wrap items-end gap-4 bg-gray-50 p-4 rounded-lg border">
              {/* Custom Date Inputs */}
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-1">From</label>
                <input type="date" value={dateFrom} onChange={(e) => setDateFrom(e.target.value)} className="text-xs p-2 border rounded bg-white" />
              </div>
              <div className="flex flex-col">
                <label className="text-[10px] font-black text-gray-400 uppercase mb-1">To</label>
                <input type="date" value={dateTo} onChange={(e) => setDateTo(e.target.value)} className="text-xs p-2 border rounded bg-white" />
              </div>

              <button onClick={fetchData} className="bg-gray-900 text-white px-4 py-2 rounded text-xs font-bold hover:bg-blue-600 transition">Fetch</button>

              <div className="flex gap-1 border-l pl-4">
                <button onClick={() => handleQuickFilter('WTD')} className="px-3 py-2 bg-white border rounded text-[10px] font-black hover:bg-blue-50">WTD</button>
                <button onClick={() => handleQuickFilter('MTD')} className="px-3 py-2 bg-white border rounded text-[10px] font-black hover:bg-blue-50">MTD</button>
              </div>

              {/* Status Toggle */}
              <div className="flex items-center gap-3 border-l pl-4">
                 <span className={`text-[10px] font-black uppercase ${!showOnlyUnreconciled ? 'text-blue-600' : 'text-gray-300'}`}>All</span>
                 <button 
                  onClick={() => setShowOnlyUnreconciled(!showOnlyUnreconciled)}
                  className={`w-12 h-6 rounded-full relative transition-colors ${showOnlyUnreconciled ? 'bg-blue-600' : 'bg-gray-300'}`}
                 >
                    <div className={`absolute top-1 w-4 h-4 bg-white rounded-full transition-all ${showOnlyUnreconciled ? 'left-7' : 'left-1'}`} />
                 </button>
                 <span className={`text-[10px] font-black uppercase ${showOnlyUnreconciled ? 'text-blue-600' : 'text-gray-300'}`}>Pending</span>
              </div>
            </div>
          </div>
        </div>

        {/* DATA TABLE */}
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead>
              <tr className="bg-gray-50 text-[10px] font-black uppercase text-gray-500 border-b">
                <th className="p-4">Date / Station</th>
                <th className="p-4">System Drop</th>
                <th className="p-4">WS Label</th>
                <th className="p-4">Variance</th>
                <th className="p-4">Counted Amount</th>
                <th className="p-4">Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredList.length > 0 ? filteredList.map(item => (
                <tr key={item.id} className="border-b hover:bg-blue-50/30 transition-colors">
                  <td className="p-4">
                    <div className="text-xs font-black">{item.date}</div>
                    <div className="text-[10px] text-blue-600 font-bold uppercase">{item.workstation} | {item.user_name}</div>
                  </td>
                  <td className="p-4">
                    <button 
                      onClick={() => setSelectedImage(item.label_image_url)}
                      className="text-sm font-black text-gray-900 border-b-2 border-dotted border-blue-400 hover:text-blue-600"
                    >
                      ${item.system_drop_amount}
                    </button>
                    <div className="text-[9px] text-gray-400 uppercase mt-1">Click to view label</div>
                  </td>
                  <td className="p-4 text-sm font-bold text-gray-600">${item.ws_label_amount}</td>
                  <td className={`p-4 text-sm font-black ${parseFloat(item.variance) !== 0 ? 'text-red-500' : 'text-gray-300'}`}>
                    ${item.variance}
                  </td>
                  <td className="p-4">
                    {item.is_reconciled ? (
                      <span className="text-sm font-black text-green-600">${item.admin_count_amount}</span>
                    ) : (
                      <div className="flex items-center bg-white border rounded px-2 py-1 w-32 focus-within:ring-2 ring-blue-500">
                        <span className="text-gray-400 mr-1">$</span>
                        <input 
                          type="number"
                          step="0.01"
                          className="w-full text-sm font-bold outline-none"
                          onChange={(e) => setAdminCounts({...adminCounts, [item.id]: e.target.value})}
                        />
                      </div>
                    )}
                  </td>
                  <td className="p-4">
                    {item.is_reconciled ? (
                      <div className="flex items-center gap-1 text-green-600">
                        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" /></svg>
                        <span className="text-[10px] font-black uppercase">Reconciled</span>
                      </div>
                    ) : (
                      <button 
                        onClick={() => handleReconcile(item)}
                        className="bg-blue-600 text-white text-[10px] font-black px-4 py-2 rounded-lg hover:bg-blue-700 shadow-md transition-all active:scale-95"
                      >
                        RECONCILE
                      </button>
                    )}
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan="6" className="p-20 text-center text-gray-400 text-xs italic font-bold uppercase tracking-widest">
                    {loading ? "Loading Records..." : "No records found for this period"}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* MODAL FOR IMAGE */}
      {selectedImage && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 p-4 backdrop-blur-sm">
          <div className="relative max-w-4xl w-full bg-white rounded-lg p-2 overflow-hidden shadow-2xl">
            <button 
              onClick={() => setSelectedImage(null)}
              className="absolute top-4 right-4 bg-red-600 text-white w-8 h-8 rounded-full font-bold flex items-center justify-center hover:bg-red-700 z-10"
            >
              âœ•
            </button>
            <div className="max-h-[80vh] overflow-y-auto">
              <img 
                src={selectedImage || ''} 
                alt="WS Label" 
                className="w-full h-auto"
                onError={(e) => console.error("Image failed to load:", e.target.src)}
              />
            </div>
            <div className="p-4 bg-gray-50 text-center">
              <p className="text-xs font-black text-gray-500 uppercase tracking-tighter">Verified Workstation Receipt Label</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CashDropValidation;
