import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
function CdValidation() {
    useEffect(() => {
    const token = localStorage.getItem('access_token');
    try {
        if (token) { 
          console.log("User is logged in.");
        }
        else{
            //alert("You are not logged in. Please log in to access the dashboard.");
        }
    } catch (error) {
        console.error("Error checking login status:", error);
    }}, []);

    useEffect(() => {
        const fetchUserDetails = async () => {
          try {
            const response = await fetch('http://localhost:8000/api/auth/users/me/', {
              headers: {
                'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
              },
            });
            if (!response.ok) throw new Error('Failed to fetch user details');
            const userData = await response.json();
            setIsAdmin(userData.is_admin);
            setLoading(false);
          } catch (err) {
            setError('Please log in to continue.');
            navigate('/login');
          }
        };
        fetchUserDetails();
      }, [navigate]);
  return (
    <div>
      <h1>CD Validation Page</h1>
      <p>This is where CD validation will take place.</p>
    </div>  
  );
}
export default CdValidation;
