import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
function CdValidation() {
    const [selectedDateFrom, setSelectedDateFrom] = useState(new Date().toISOString().slice(0, 10));
    const [selectedDateTo, setSelectedDateTo] = useState(new Date().toISOString().slice(0, 10));



    const fetchData = async () => {
        // Implement data fetching logic here
    }

    const handleDateFromChange = (e) => {
        setSelectedDateFrom(e.target.value);
      };

      const handleDateToChange = (e) => {
        setSelectedDateTo(e.target.value);
      };
  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 flex items-center justify-center p-4 font-sans">
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-2xl p-8 md:p-12 w-full max-w-6xl items-center">
            <h2 className="text-4xl font-semibold text-blue-800 dark:text-blue-200 mb-6 border-b pb-3 border-blue-200 dark:border-gray-600"> Validate Cash Drops</h2>
        </div>
        <div className="mb-8 grid justify-center">
          <div className="flex flex-col sm:flex-row items-center space-y-4 sm:space-y-0 sm:space-x-4">
            <div className="flex flex-col">
              <label htmlFor="date" className="text-gray-700 dark:text-gray-300 font-medium mb-1">
                Select Date From:
              </label>
              <input
                id="datefrom"
                type="date"
                value={selectedDateFrom}
                onChange={handleDateFromChange}
                className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:text-white"
              />
            </div>
            <div className="flex flex-col">
              <label htmlFor="date" className="text-gray-700 dark:text-gray-300 font-medium mb-1">
                Select Date To:
              </label>
              <input
                id="dateto"
                type="date"
                value={selectedDateTo}
                onChange={handleDateToChange}
                className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:text-white"
              />
            </div>
            <button
              onClick={fetchData}
              className="px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-blue-300 dark:focus:ring-blue-800"
            >
              Fetch Data
            </button>
          </div>
        </div>
    </div>  
  );
}
export default CdValidation;
