import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CdDashboard() {
  const navigate = useNavigate();
  const [selectedDateFrom, setSelectedDateFrom] = useState(new Date().toISOString().slice(0, 10));
  const [selectedDateTo, setSelectedDateTo] = useState(new Date().toISOString().slice(0, 10));
  const [cashDrops, setCashDrops] = useState([]);
  const [cashDrawers, setCashDrawers] = useState([]);
  const [activeDate, setActiveDate] = useState(null); // Currently clicked date from sidebar
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Authentication check
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/auth/users/me/', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        });
        if (!response.ok) throw new Error('Failed to fetch user details');
        const userData = await response.json();
        setIsAdmin(userData.is_admin);
        setLoading(false);
      } catch (err) {
        navigate('/login');
      }
    };
    fetchUserDetails();
  }, [navigate]);

  // General Fetch Function
  const fetchData = async (from, to) => {
    setError('');
    setActiveDate(null); // Reset active view
    try {
      const [dropResponse, drawerResponse] = await Promise.all([
        fetch(`http://localhost:8000/api/cash-drop-app1/cash-drop/?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
        fetch(`http://localhost:8000/api/cash-drop-app1/cash-drawer/?datefrom=${from}&dateto=${to}`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        }),
      ]);

      if (!dropResponse.ok || !drawerResponse.ok) throw new Error('Failed to fetch data');

      const dropData = await dropResponse.json();
      const drawerData = await drawerResponse.json();

      setCashDrops(dropData);
      setCashDrawers(drawerData);

      // If we have data, default to the most recent date found
      if (dropData.length > 0) setActiveDate(dropData[0].date);
    } catch (err) {
      setError('Error fetching data: ' + err.message);
    }
  };

  // Date Range Logic
  const handleMTD = () => {
    const now = new Date();
    const firstDay = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().slice(0, 10);
    const today = now.toISOString().slice(0, 10);
    setSelectedDateFrom(firstDay);
    setSelectedDateTo(today);
    fetchData(firstDay, today);
  };

  const handleWTD = () => {
    const now = new Date();
    const day = now.getDay(); 
    const diff = now.getDate() - day + (day === 0 ? -6 : 1); // Adjust for Monday start
    const firstDay = new Date(now.setDate(diff)).toISOString().slice(0, 10);
    const today = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(firstDay);
    setSelectedDateTo(today);
    fetchData(firstDay, today);
  };

  const handleYTD = () => {
    const firstDay = new Date(new Date().getFullYear(), 0, 1).toISOString().slice(0, 10);
    const today = new Date().toISOString().slice(0, 10);
    setSelectedDateFrom(firstDay);
    setSelectedDateTo(today);
    fetchData(firstDay, today);
  };

  // Get unique dates from both drops and drawers for the sidebar
  const uniqueDates = [...new Set([...cashDrops.map(d => d.date), ...cashDrawers.map(d => d.date)])].sort().reverse();

  if (loading) return <div className="min-h-screen flex items-center justify-center">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 p-4">
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-2xl p-6 max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-blue-600 dark:text-white text-center mb-8">Cash Drop Dashboard</h2>

        {/* Control Panel */}
        <div className="flex flex-wrap items-end justify-center gap-4 mb-10 pb-6 border-b border-gray-200 dark:border-gray-700">
          <div className="flex flex-col">
            <label className="text-sm font-bold text-gray-500 mb-1 uppercase">From</label>
            <input type="date" value={selectedDateFrom} onChange={e => setSelectedDateFrom(e.target.value)} className="border rounded p-2 dark:bg-gray-700 dark:text-white" />
          </div>
          <div className="flex flex-col">
            <label className="text-sm font-bold text-gray-500 mb-1 uppercase">To</label>
            <input type="date" value={selectedDateTo} onChange={e => setSelectedDateTo(e.target.value)} className="border rounded p-2 dark:bg-gray-700 dark:text-white" />
          </div>
          <button onClick={() => fetchData(selectedDateFrom, selectedDateTo)} className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition">Fetch</button>

          <div className="flex gap-2 ml-4 border-l pl-4 border-gray-300">
            <button onClick={handleWTD} className="bg-gray-200 dark:bg-gray-600 px-4 py-2 rounded text-sm font-bold hover:bg-blue-100 transition">Week</button>
            <button onClick={handleMTD} className="bg-gray-200 dark:bg-gray-600 px-4 py-2 rounded text-sm font-bold hover:bg-blue-100 transition">Month</button>
            <button onClick={handleYTD} className="bg-gray-200 dark:bg-gray-600 px-4 py-2 rounded text-sm font-bold hover:bg-blue-100 transition">Year</button>
          </div>
        </div>

        <div className="flex flex-col md:flex-row gap-6">
          {/* Sidebar: Date Buttons */}
          <div className="w-full md:w-48 flex-shrink-0">
            <h4 className="text-xs font-bold text-gray-400 mb-3 uppercase tracking-widest">Available Dates</h4>
            <div className="max-h-[600px] overflow-y-auto pr-2 space-y-2 custom-scrollbar">
              {uniqueDates.map(date => (
                <button
                  key={date}
                  onClick={() => setActiveDate(date)}
                  className={`w-full text-left px-4 py-3 rounded-lg text-sm font-medium transition ${activeDate === date ? 'bg-blue-600 text-white shadow-md' : 'bg-gray-50 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-blue-50'}`}
                >
                  {new Date(date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' })}
                </button>
              ))}
              {uniqueDates.length === 0 && <p className="text-xs text-gray-400 italic">No dates found.</p>}
            </div>
          </div>

          {/* Main Content: Display data for ActiveDate */}
          <div className="flex-1">
            {!activeDate ? (
              <div className="h-64 flex items-center justify-center border-2 border-dashed rounded-xl border-gray-200 text-gray-400">
                Select a date from the sidebar to view details
              </div>
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Cash Drops Column */}
                <div>
                  <h3 className="text-xl font-bold text-blue-800 dark:text-blue-300 mb-4 flex items-center gap-2">
                    <span className="w-2 h-2 bg-blue-500 rounded-full"></span> Cash Drops
                  </h3>
                  {cashDrops.filter(d => d.date === activeDate).map(drop => (
                    <div key={drop.id} className="mb-4 p-5 bg-blue-50 dark:bg-gray-700 rounded-xl border border-blue-100 dark:border-gray-600">
                      <div className="flex justify-between mb-4 border-b pb-2">
                        <span className="font-bold text-blue-900 dark:text-blue-200">{drop.workstation} / {drop.shift_number}</span>
                        <span className="font-bold text-green-600 text-lg">${drop.drop_amount}</span>
                      </div>
                      <div className="grid grid-cols-2 gap-2 text-xs">
                        {Object.entries(drop).map(([key, value]) => (
                          ['hundreds', 'fifties', 'twenties', 'tens', 'fives', 'twos', 'ones', 'half_dollars', 'quarters', 'dimes', 'nickels', 'pennies'].includes(key) && value > 0 && (
                            <div key={key} className="flex justify-between bg-white dark:bg-gray-800 p-1 px-2 rounded">
                              <span className="capitalize">{key.replace('_', ' ')}</span>
                              <span className="font-bold">{value}</span>
                            </div>
                          )
                        ))}
                      </div>
                    </div>
                  ))}
                </div>

                {/* Cash Drawers Column */}
                <div>
                  <h3 className="text-xl font-bold text-green-800 dark:text-green-300 mb-4 flex items-center gap-2">
                    <span className="w-2 h-2 bg-green-500 rounded-full"></span> Cash Drawers
                  </h3>
                  {cashDrawers.filter(d => d.date === activeDate).map(drawer => (
                    <div key={drawer.id} className="mb-4 p-5 bg-green-50 dark:bg-gray-700 rounded-xl border border-green-100 dark:border-gray-600">
                      <div className="flex justify-between mb-2">
                        <span className="font-bold text-green-900 dark:text-green-200">{drawer.workstation}</span>
                        <span className="text-xs text-gray-500">Start: ${drawer.starting_cash}</span>
                      </div>
                      <div className="text-xl font-black text-green-700 mb-4">Total: ${drawer.total_cash}</div>
                      <div className="grid grid-cols-2 gap-2 text-xs">
                        {Object.entries(drawer).map(([key, value]) => (
                          ['hundreds', 'fifties', 'twenties', 'tens', 'fives', 'twos', 'ones', 'half_dollars', 'quarters', 'dimes', 'nickels', 'pennies'].includes(key) && value > 0 && (
                            <div key={key} className="flex justify-between bg-white dark:bg-gray-800 p-1 px-2 rounded">
                              <span className="capitalize">{key.replace('_', ' ')}</span>
                              <span className="font-bold">{value}</span>
                            </div>
                          )
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default CdDashboard;
