import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function CashDrop() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    employeeName: '',
    shiftNumber: '',
    workStation: '',
    date: new Date().toISOString().slice(0, 10),
    startingCash: '200.00',
    wsLabelAmount: 0, // NEW: Workstation Label Amount
    pennies: 0, nickels: 0, dimes: 0, quarters: 0, halfDollars: 0,
    ones: 0, twos: 0, fives: 0, tens: 0, twenties: 0, fifties: 0, hundreds: 0,
  });

  const [labelImage, setLabelImage] = useState(null); // NEW: Image File State
  const [cashDropDenominations, setCashDropDenominations] = useState(null);
  const [remainingCashInDrawer, setRemainingCashInDrawer] = useState(null);
  const [denominationsError, setDenominationsError] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  const DENOMINATION_CONFIG = [
    { name: 'Hundreds', value: 100, field: 'hundreds' },
    { name: 'Fifties', value: 50, field: 'fifties' },
    { name: 'Twenties', value: 20, field: 'twenties' },
    { name: 'Tens', value: 10, field: 'tens' },
    { name: 'Fives', value: 5, field: 'fives' },
    { name: 'Twos', value: 2, field: 'twos' },
    { name: 'Ones', value: 1, field: 'ones' },
    { name: 'Half Dollars', value: 0.50, field: 'halfDollars' },
    { name: 'Quarters', value: 0.25, field: 'quarters' },
    { name: 'Dimes', value: 0.10, field: 'dimes' },
    { name: 'Nickels', value: 0.05, field: 'nickels' },
    { name: 'Pennies', value: 0.01, field: 'pennies' },
  ];

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/auth/users/me/', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` },
        });
        if (!response.ok) throw new Error('Failed to fetch user');
        const userData = await response.json();
        setFormData(prev => ({ ...prev, employeeName: userData.name }));
        setIsAdmin(userData.is_admin);
        setLoading(false);
      } catch (error) {
        navigate('/login');
      }
    };
    fetchUserDetails();
  }, [navigate]);

  // AUTO-CALCULATE DENOMINATIONS (Responsive behavior)
  useEffect(() => {
    calculateDenominations();
  }, [formData.hundreds, formData.fifties, formData.twenties, formData.tens, formData.fives, formData.twos, formData.ones, formData.halfDollars, formData.quarters, formData.dimes, formData.nickels, formData.pennies, formData.startingCash]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'number' || name === 'wsLabelAmount' ? parseFloat(value) || 0 : value,
    }));
  };

  const handleFileChange = (e) => {
    setLabelImage(e.target.files[0]);
  };

  const calculateTotalCash = () => {
    return DENOMINATION_CONFIG.reduce((acc, denom) => acc + (formData[denom.field] || 0) * denom.value, 0).toFixed(2);
  };

  const calculateCashDropAmount = () => {
    const total = parseFloat(calculateTotalCash());
    const starting = parseFloat(formData.startingCash) || 0;
    return (total - starting).toFixed(2);
  };

  const calculateVariance = () => {
    const drop = parseFloat(calculateCashDropAmount());
    const label = parseFloat(formData.wsLabelAmount) || 0;
    return (drop - label).toFixed(2);
  };

  // Check if Drop matches (Drawer - Starting) to show Submit button
  const isSubmitValid = () => {
    const dropAmount = parseFloat(calculateCashDropAmount());
    const drawerTotal = parseFloat(calculateTotalCash());
    const starting = parseFloat(formData.startingCash) || 0;
    const isMathCorrect = Math.abs(dropAmount - (drawerTotal - starting)) < 0.01;
    return isMathCorrect && dropAmount > 0 && formData.workStation && labelImage;
  };

  const calculateDenominations = () => {
    const amountToDrop = parseFloat(calculateCashDropAmount());
    if (isNaN(amountToDrop) || amountToDrop <= 0) {
      setCashDropDenominations(null);
      return;
    }

    let remainingDropAmount = Math.round(amountToDrop * 100);
    const dropBreakdown = {};
    const finalDrawerRemaining = {};

    DENOMINATION_CONFIG.forEach(denom => {
      const denomValueCents = Math.round(denom.value * 100);
      const availableCount = formData[denom.field] || 0;
      let countForDrop = 0;

      if (denomValueCents > 0 && remainingDropAmount >= denomValueCents) {
        countForDrop = Math.min(Math.floor(remainingDropAmount / denomValueCents), availableCount);
        remainingDropAmount -= countForDrop * denomValueCents;
      }
      dropBreakdown[denom.field] = countForDrop;
      finalDrawerRemaining[denom.field] = availableCount - countForDrop;
    });

    setCashDropDenominations(dropBreakdown);
    setRemainingCashInDrawer(finalDrawerRemaining);
    setDenominationsError(remainingDropAmount > 0 ? `Exact change not possible. Variance: $${(remainingDropAmount / 100).toFixed(2)}` : '');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');

    try {
      // Create FormData to handle image upload
      const uploadData = new FormData();
      uploadData.append('workstation', formData.workStation);
      uploadData.append('shift_number', formData.shiftNumber);
      uploadData.append('date', formData.date);
      uploadData.append('starting_cash', formData.startingCash);
      uploadData.append('total_cash', calculateTotalCash());
      uploadData.append('drop_amount', calculateCashDropAmount());
      uploadData.append('ws_label_amount', formData.wsLabelAmount);
      uploadData.append('variance', calculateVariance());
      if (labelImage) uploadData.append('label_image', labelImage);

      // Denominations for drawer
      DENOMINATION_CONFIG.forEach(d => uploadData.append(d.field, formData[d.field]));
      // Denominations for drop (prefixed or handled by backend)
      Object.keys(cashDropDenominations).forEach(key => uploadData.append(`drop_${key}`, cashDropDenominations[key]));

      const response = await fetch('http://localhost:8000/api/cash-drop-app1/cash-drop-submit/', {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` },
        body: uploadData, // Browser sets Content-Type to multipart/form-data automatically
      });

      if (!response.ok) throw new Error('Submission failed');
      alert('Cash drop submitted successfully!');
      navigate('/cd-dashboard');
    } catch (error) {
      alert(error.message);
    }
  };

  if (loading) return <div className="min-h-screen bg-gray-100 flex items-center justify-center">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 p-4 font-sans">
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-2xl p-8 max-w-7xl mx-auto">
        <h2 className="text-3xl font-black text-pink-600 text-center mb-8 uppercase italic">CashDrop Terminal</h2>

        {/* 1. Cashier Details */}
        <div className="bg-blue-50 dark:bg-gray-700/50 p-6 rounded-lg mb-8 grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
          <div className="flex flex-col"><label className="text-xs font-bold text-gray-500 mb-1">Employee</label><input type="text" value={formData.employeeName} readOnly className="p-2 border rounded bg-gray-100 dark:bg-gray-600" /></div>
          <div className="flex flex-col"><label className="text-xs font-bold text-gray-500 mb-1">Shift</label><input type="text" name="shiftNumber" onChange={handleChange} className="p-2 border rounded dark:bg-gray-700" /></div>
          <div className="flex flex-col"><label className="text-xs font-bold text-gray-500 mb-1">Workstation</label><input type="text" name="workStation" onChange={handleChange} className="p-2 border rounded dark:bg-gray-700" /></div>
          <div className="flex flex-col"><label className="text-xs font-bold text-gray-500 mb-1">Date</label><input type="date" name="date" value={formData.date} onChange={handleChange} className="p-2 border rounded dark:bg-gray-700" /></div>
          <div className="flex flex-col"><label className="text-xs font-bold text-gray-500 mb-1">Starting Cash</label><input type="number" name="startingCash" value={formData.startingCash} readOnly={!isAdmin} onChange={handleChange} className="p-2 border rounded bg-gray-50 dark:bg-gray-600" /></div>
          <div className="flex flex-col"><label className="text-xs font-bold text-pink-600 mb-1">WS Label Amount</label><input type="number" name="wsLabelAmount" onChange={handleChange} className="p-2 border-2 border-pink-200 rounded dark:bg-gray-700 font-bold" /></div>
        </div>

        {/* 2. Main Entry Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Input Section */}
          <div className="bg-green-50 dark:bg-gray-700 p-6 rounded-lg">
            <h3 className="font-bold mb-4 border-b pb-2 uppercase text-green-700">Cash Drawer Entry</h3>
            <div className="space-y-2">
              {DENOMINATION_CONFIG.map(denom => (
                <div key={denom.field} className="flex justify-between items-center">
                  <span className="text-sm font-medium">{denom.name}</span>
                  <input type="number" name={denom.field} value={formData[denom.field]} onChange={handleChange} className="w-20 p-1 border rounded text-right" min="0" />
                </div>
              ))}
            </div>
            <div className="mt-6 space-y-2 text-sm font-bold pt-4 border-t">
              <div className="flex justify-between"><span>Total Drawer:</span> <span className="text-blue-600">${calculateTotalCash()}</span></div>
              <div className="flex justify-between"><span>Net Drop:</span> <span className="text-green-600">${calculateCashDropAmount()}</span></div>
              <div className="flex justify-between p-2 bg-pink-100 rounded text-pink-700"><span>Variance:</span> <span>${calculateVariance()}</span></div>
            </div>
          </div>

          {/* Responsive Drop Breakdown */}
          <div className="bg-purple-50 dark:bg-gray-700 p-6 rounded-lg">
            <h3 className="font-bold mb-4 border-b pb-2 uppercase text-purple-700">Auto-Drop Breakdown</h3>
            <div className="space-y-2">
              {cashDropDenominations ? DENOMINATION_CONFIG.map(denom => {
                const count = cashDropDenominations[denom.field];
                return count > 0 ? (
                  <div key={denom.field} className="flex justify-between p-2 bg-white dark:bg-gray-800 rounded shadow-sm">
                    <span className="text-xs font-bold">{denom.name}</span>
                    <span className="text-purple-600">x {count}</span>
                  </div>
                ) : null;
              }) : <p className="text-gray-400 text-xs text-center mt-10 italic">Enter drawer amounts to see drop...</p>}
            </div>
          </div>

          {/* Remaining in Drawer */}
          <div className="bg-yellow-50 dark:bg-gray-700 p-6 rounded-lg">
            <h3 className="font-bold mb-4 border-b pb-2 uppercase text-yellow-700">Remaining in Drawer</h3>
            <div className="space-y-1">
              {remainingCashInDrawer ? DENOMINATION_CONFIG.map(denom => (
                <div key={denom.field} className="flex justify-between text-xs py-1 border-b border-yellow-100">
                  <span>{denom.name}</span>
                  <span className="font-bold">{remainingCashInDrawer[denom.field]}</span>
                </div>
              )) : <p className="text-gray-400 text-xs text-center mt-10 italic">Calculated automatically...</p>}
            </div>
          </div>
        </div>

        {/* 3. Image Upload and Submit */}
        <div className="mt-10 p-6 border-t flex flex-col items-center gap-6">
          <div className="flex flex-col items-center gap-4">
             <label className={`cursor-pointer flex flex-col items-center p-4 border-2 border-dashed rounded-lg transition ${labelImage ? 'border-green-500 bg-green-50' : 'border-pink-300 hover:bg-pink-50'}`}>
                <span className="text-pink-600 font-bold mb-1">{labelImage ? '‚úÖ Label Uploaded' : 'üìÅ Upload Workstation Label'}</span>
                <span className="text-[10px] text-gray-400">{labelImage ? labelImage.name : 'Click to select image'}</span>
                <input type="file" className="hidden" onChange={handleFileChange} accept="image/*" />
             </label>
          </div>

          {isSubmitValid() ? (
            <button onClick={handleSubmit} className="px-12 py-4 bg-blue-600 hover:bg-blue-700 text-white font-black rounded-lg shadow-xl transform transition hover:scale-105">
              SUBMIT CASH DROP
            </button>
          ) : (
            <div className="text-center">
              <p className="text-xs text-red-500 font-bold bg-red-50 p-3 rounded">
                {!labelImage ? "‚ö†Ô∏è Please upload label image" : "‚ö†Ô∏è Math mismatch: Cash Drop must equal (Total - Starting Cash)"}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default CashDrop;
